1.customPhraseDescTS:
// 根据属性名称值编写自定义短语描述，如："我的短语是"+attrs.xxx
// 使用下拉框组件的变量attrs.xxx有对应的attrs.xxxItems的属性以便获得标签名集合
// 获取自定义模块的数据名称的函数：getModuleDataName(moduleID:number,dataID:number):string;
// 获取各种列表内数据名称的函数：getTypeDataListName(typeID: number, dataID: number):string;
// -- typeID ：0-全局变量 1-全局开关 2-全局字符串 3-玩家变量 4-玩家开关 5-玩家字符串
//           ：6-场景列表 7-场景对象模型列表 8-图块列表 9-行走图列表 10-行走图动作列表 11-立绘列表 12-立绘表情列表
//           ：13-公共事件 14-对话框列表 15-动画列表 16-动画信号列表 17-界面列表 18-数据结构列表 19-自定义模块列表
//           ：20-自定义命令列表 21-自定义行为列表 22-自动元件列表 23-行走图部件列表 24-立绘部件列表
var typeFuncs = [-1, -1, 9, 11, 15, 17, 14];
var str = "";
// 显示加载界面
if (attrs.isShowLoadingUI) {
    str += `显示进度：「${attrs.bindingUI}-${getTypeDataListName(17, attrs.bindingUI)}」`;
}
else {
    str += "静默加载"
}

for (var i = 0; i < attrs.preloadAssets.length; i++) {
    var preloadAsset = attrs.preloadAssets[i];
    var funcID = typeFuncs[preloadAsset.assetType];
    var assetValue = preloadAsset["asset" + preloadAsset.assetType];
    if (funcID != -1) {
        var assetName = `${assetValue}-${getTypeDataListName(funcID, assetValue)}`;
    }
    else {
        assetName = assetValue;
    }
    if (preloadAsset.assetType < 7) {
        str += "\n " + preloadAsset.assetTypeItems[preloadAsset.assetType] + ":「" + assetName + "」";
    }
    else {
        str += "\n 「" + preloadAsset.assetTypeItems[preloadAsset.assetType] + "」";
    }
}
str;

1.customPhraseDescJS:
var typeFuncs = [-1, -1, 9, 11, 15, 17, 14];
var str = "";
if (attrs.isShowLoadingUI) {
    str += "\u663E\u793A\u8FDB\u5EA6\uFF1A\u300C" + attrs.bindingUI + "-" + getTypeDataListName(17, attrs.bindingUI) + "\u300D";
}
else {
    str += "静默加载";
}
for (var i = 0; i < attrs.preloadAssets.length; i++) {
    var preloadAsset = attrs.preloadAssets[i];
    var funcID = typeFuncs[preloadAsset.assetType];
    var assetValue = preloadAsset["asset" + preloadAsset.assetType];
    if (funcID != -1) {
        var assetName = assetValue + "-" + getTypeDataListName(funcID, assetValue);
    }
    else {
        assetName = assetValue;
    }
    if (preloadAsset.assetType < 7) {
        str += "\n " + preloadAsset.assetTypeItems[preloadAsset.assetType] + ":「" + assetName + "」";
    }
    else {
        str += "\n 「" + preloadAsset.assetTypeItems[preloadAsset.assetType] + "」";
    }
}
str;

