3.customPhraseDescTS:
// 根据属性名称值编写自定义短语描述，如："我的短语是"+attrs.xxx
// 使用下拉框组件的变量attrs.xxx有对应的attrs.xxxItems的属性以便获得标签名集合
// 获取自定义模块的数据名称的函数：getModuleDataName(moduleID:number,dataID:number):string;
// 获取各种列表内数据名称的函数：getTypeDataListName(typeID: number, dataID: number):string;
// -- typeID ：0-全局变量 1-全局开关 2-全局字符串 3-玩家变量 4-玩家开关 5-玩家字符串
//           ：6-场景列表 7-场景对象模型列表 8-图块列表 9-行走图列表 10-行走图动作列表 11-立绘列表 12-立绘表情列表
//           ：13-公共事件 14-对话框列表 15-动画列表 16-动画信号列表 17-界面列表 18-数据结构列表 19-自定义模块列表
//           ：20-自定义命令列表 21-自定义行为列表 22-自动元件列表 23-行走图部件列表 24-立绘部件列表
var str = `「${attrs.typeItems[attrs.type]}」当`;
// --- 
if (attrs.isMulKey <= 1) {
    if (attrs.CTRL) str += "CTRL+"
    if (attrs.SHIFT) str += "SHIFT+"
    if (attrs.ALT) str += "ALT+"
}
str += "「";
var evType = "";
if (attrs.isMulKey == 1) {
    var keyNames = [];
    for (var i = 0; i < attrs.keys.length; i++) {
        keyNames.push(getKeyName(attrs.keys[i]));
    }
    str += keyNames.join("/");
    evType = attrs.evType2Items[attrs.evType2];
}
else if (attrs.isMulKey == 0) {
    str += getKeyName(attrs.key);
    evType = attrs.evTypeItems[attrs.evType];
}
else if (attrs.isMulKey == 2) {
    str += attrs.systemKeyItems[attrs.systemKey];
    evType = attrs.evTypeItems[attrs.evType];
}
else if (attrs.isMulKey == 3) {
    var keyNames = [];
    for (var i = 0; i < attrs.systemKeys.length; i++) {
        var sysKey = attrs.systemKeys[i];
        keyNames.push(attrs.systemKeyItems[sysKey]);
    }
    str += keyNames.join("/");
    evType = attrs.evType2Items[attrs.evType2];
}

str += "」" + evType;
str += "执行：【" + getFeDataMessage() + "】";
if (attrs.recordListen) {
    str += ` 监听标识「${attrs.recordListenVar}-${getTypeDataListName(5, attrs.recordListenVar)}」`
}
str;

function getKeyName(keyCode: number) {
    for (var k in Keyboard) {
        if (Keyboard[k] == keyCode) {
            return `${k}`;
        }
    }
    return "--/--";
}
function getFeDataMessage() {
    if (!attrs.eventPage) return "无";
    var startIdx = attrs.eventPage.indexOf(String.fromCharCode(5));
    var feName = attrs.eventPage.substr(0, startIdx);
    return feName ? feName : "未命名片段事件";
}

