2012.customPhraseDescTS:
// 根据属性名称值编写自定义短语描述，如："我的短语是"+attrs.xxx
// 使用下拉框组件的变量attrs.xxx有对应的attrs.xxxItems的属性以便获得标签名集合
// 获取自定义模块的数据名称的函数：getModuleDataName(moduleID:number,dataID:number):string;
// 获取各种列表内数据名称的函数：getTypeDataListName(typeID: number, dataID: number):string;
// -- typeID ：0-全局变量 1-全局开关 2-全局字符串 3-玩家变量 4-玩家开关 5-玩家字符串
//           ：6-场景列表 7-场景对象模型列表 8-图块列表 9-行走图列表 10-行走图动作列表 11-立绘列表 12-立绘表情列表
//           ：13-公共事件 14-对话框列表 15-动画列表 16-动画信号列表 17-界面列表 18-数据结构列表 19-自定义模块列表
//           ：20-自定义命令列表 21-自定义行为列表 22-自动元件列表 23-行走图部件列表 24-立绘部件列表
let str = "";
// 商店货物清单
str += "商店货物清单 商品数：" + attrs.goodsList.length.toString() + "\n";
for (let i = 0; i < attrs.goodsList.length; i++) {
    let goods = attrs.goodsList[i];
    if (goods.isEquip) {
        var moduleID = 4;
        var itemID = goods.equip;
        var itemTypeName = "装备";
    }
    else {
        var moduleID = 5;
        var itemID = goods.item;
        itemTypeName = "道具";
    }
    // 道具
    let itemName = itemID + "-" + getModuleDataName(moduleID, itemID) + "」";
    let itemNameLength = 0;
    for (let j = 0; j < itemName.length; j++) {
        if (itemName.charCodeAt(j) < 128) {
            itemNameLength++;
        }
        else {
            itemNameLength += 2;
        }
    }
    for (let j = itemNameLength; j < 16; j++) {
        itemName += " ";
    }
    str += itemTypeName + "：「" + itemName;
    // 数量
    str += " 数量：";
    let itemNum: string = "";
    if (goods.numberType == 0) {
        itemNum = " ∞";
    }
    else if (goods.numberType == 1) {
        itemNum = " " + goods.number.toString();
    }
    else {
        itemNum = "「" + goods.numberVar + "-" + getTypeDataListName(3, goods.numberVar) + "」";
    }
    let itemNumLength = 0;
    for (let j = 0; j < itemNum.length; j++) {
        if (itemNum.charCodeAt(j) < 128) {
            itemNumLength++;
        }
        else {
            itemNumLength += 2;
        }
    }
    for (let j = itemNumLength; j < 16; j++) {
        itemNum += " ";
    }
    str += itemNum;
    // 价格
    str += "价格：";
    if (goods.priceType == 0) {
        str += " 默认";
    }
    else if (goods.priceType == 1) {
        str += " " + goods.price;
    }
    else {
        str += "「" + goods.priceVar + "-" + getTypeDataListName(3, goods.priceVar) + "」";
    }
    //换行
    str += "\n";
}
// 个性化设置
if (attrs.nameWhenInfinite != "∞") str += "\n- 无限数量代称：" + attrs.nameWhenInfinite;
if (!attrs.enableSell) str += "\n- 不允许出售";
else {
    str += "\n- 售价折扣：" + attrs.discount.toString();
}
// 输出
str;

