//------------------------------------------------------------------------------------------------------
// 通道数据
//------------------------------------------------------------------------------------------------------
declare class StandAvatarPassageData {
    // ====> system 必要项
    /**
     * 通道编号
     */
    passageID: number;
    /**
     * 类别，一般用于区分显示对象，如图片、动画、立绘、界面都是属于不同的类别
     */
    type: string;
    /**
     * 记录关联的指令
     */
    cmd: Command;
    /**
     * 当通道覆盖时调用的函数，以便清理掉。如显示了2号图片或移动了2号图片，再次显示2号图片时会清理掉此前显示的图片或移动行为
     */
    onCoverPassage: Function;

    // ====> custom 自定义属性
    passageIDUseVar: boolean;
    standAvatar: number;
    expression: number;
    displayObject: UIStandAvatar;
    displayObjectFromShow: boolean; // 区分显示对象来源，是否来自显示图片，否则来自移动图片
    dpX: number;
    dpY: number;
    dpZ: number;
    dpScaleX: number;
    dpScaleY: number;
    rotation: number;
    opacity: number;
    playType: number;
    avatarFPS: number;
    avatarFrame: number;

    objectUseVar: boolean;
    refObjectEnabled: boolean;
    refObject: number;
    lastStandAvatarCmdParams: ThisCommandParams; // 记录上一个关联的指令的参数
}
//------------------------------------------------------------------------------------------------------
// 自定义变量
//------------------------------------------------------------------------------------------------------
var passageType = "gcStandAvatar";
var PLUGIN_CUSTOM_COMMAND_showStandAvatar: number = 3006;
var PLUGIN_CUSTOM_COMMAND_moveStandAvatar: number = 3007;
var PLUGIN_CUSTOM_COMMAND_earsePicture: number = 3008;
var newCmdParams;
var newCmdData: StandAvatarPassageData;

//------------------------------------------------------------------------------------------------------
// [everyone used command] 逐指令行目执行
//------------------------------------------------------------------------------------------------------
/**
 * 逐指令执行：对应事件下按照从上至下的顺序逐行执行，如果是该自定义指令时则会执行该函数
 * 该函数用于编辑器预览和播放，如果分歧面板中筛选不经过的事件指令则不会执行该函数。
 * 比如事件页中有3个该指令，则按照顺序执行3次该函数
 * @param cmdParams 指令的自定义参数数据，根据用户设定值
 * @param cmd 对应的指令对象
 * @param isPlayMode 是否是播放模式，否则是普通的预览模式
 */
function runCommand(cmdParams: ThisCommandParams, cmd: Command, isPlayMode: boolean) {
    // 使用变量作为通道的话则忽略此指令执行
    if (cmdParams.passageIDUseVar) {
        return;
    }
    // -- 获取通道数据
    var passageData: StandAvatarPassageData = imageLayerPassageData[cmdParams.passageID];
    // -- 播放模式下
    if (isPlayMode) {
        // 覆盖通道，调用上一个占用该通道的指令的覆盖函数（不一定是该指令）
        if (passageData) {
            passageData.onCoverPassage && passageData.onCoverPassage.apply(this, [cmdParams.passageID, passageData]);
        }
    }
    // -- 获取储存的通道，没有的话则新建
    passageData = imageLayerPassageData[cmdParams.passageID] = {} as any;

    // 写入数据：系统必要项
    passageData.passageID = cmdParams.passageID;
    passageData.type = passageType;
    passageData.cmd = cmd;
    passageData.lastStandAvatarCmdParams = cmdParams;
    passageData.objectUseVar = cmdParams.objectUseVar;
    passageData.refObjectEnabled = cmdParams.refObjectEnabled;
    passageData.refObject = cmdParams.refObject;
    // 写入数据：自定义属性（预览不支持变量效果显示）
    if (cmdParams.objectUseVar) {
        if (cmdParams.refObjectEnabled) {
            passageData.standAvatar = cmdParams.refObject ? cmdParams.refObject : 0;
        }
        else {
            passageData.standAvatar = 0;
        }
    }
    else {
        passageData.standAvatar = cmdParams.standAvatar ? cmdParams.standAvatar : 0;
    }
    passageData.dpX = cmdParams.dpX;
    passageData.dpY = cmdParams.dpY;
    passageData.dpZ = cmdParams.dpZ;
    passageData.dpScaleX = cmdParams.dpScaleX;
    passageData.dpScaleY = cmdParams.dpScaleY;
    passageData.rotation = cmdParams.rotation;
    passageData.opacity = cmdParams.opacity;
    passageData.displayObjectFromShow = true;
    passageData.playType = cmdParams.playType;
    passageData.avatarFPS = cmdParams.avatarFPS;
    passageData.avatarFrame = cmdParams.avatarFrame;
    passageData.expression = cmdParams.expression ? cmdParams.expression : 0;

    // 播放模式的情况：需要创建对象以便播放
    if (isPlayMode) {
        // 记录覆盖函数，以便其他指令可以覆盖该通道时
        passageData.onCoverPassage = imageLayerPassageClear;
        // 创建图片显示对象
        var a = new UIStandAvatar();
        a.avatar.syncLoadWhenAssetExist = true;
        // 设定显示优先度，以便在同Z轴时，编号越大，显示在越前方
        a.useDPCoordScaleMode = true;
        a.dpDisplayPriority = passageData.passageID;
        a.avatarID = passageData.standAvatar;
        a.data = passageData;
        imageLayer.addChild(a);
        a.useDPCoord = true;
        a.dpX = passageData.dpX;
        a.dpY = passageData.dpY;
        a.dpZ = passageData.dpZ;
        a.dpScaleX = passageData.dpScaleX;
        a.dpScaleY = passageData.dpScaleY;
        a.rotation1 = passageData.rotation;
        a.opacity = passageData.opacity;
        a.playOnce = passageData.playType == 1;
        a.avatarFPS = passageData.avatarFPS;
        a.avatarFrame = passageData.avatarFrame;
        a.avatar.actionID = passageData.expression;
        a.isPlay = passageData.playType != 0;
        // 刷新坐标
        a.dpCoordToRealCoord();
        // 记录显示对象
        passageData.displayObject = a;
    }
}
/**
 * 编辑器内预览播放时预加载
 */
function inEditorPlayPreload(onFin: Function, cmdParams: ThisCommandParams) {
    var toAvatar: number;
    // 使用变量的情况下
    if (cmdParams.objectUseVar) {
        if (cmdParams.refObjectEnabled) {
            toAvatar = cmdParams.refObject ? cmdParams.refObject : 0;
        }
        else {
            toAvatar = 0;
        }
    }
    // 普通显示
    else {
        toAvatar = cmdParams.standAvatar ? cmdParams.standAvatar : 0;
    }
    AssetManager.preLoadStandAvatarAsset(toAvatar, Callback.New(onFin, this), true);
}
//------------------------------------------------------------------------------------------------------
// 属性面板修改：当修改该指令的属性时，只会执行该指令对应的「指令源」的这些函数
// 如A指令来自自定义指令32号，每次通过属性面板快速修改A指令的参数b时，则会执行32号指令的下面2个函数
// [current used command] 
//------------------------------------------------------------------------------------------------------
/**
 * 当属性面板中的属性修改前
 * @param cmd 对应的指令
 * @param cmdParams 对应指令的参数 
 * @param varName 修改的属性名
 */
function onCommandAttributeBeforeChange(cmd: Command, cmdParams: ThisCommandParams, varName: string) {
    // 修改了通道的话，清理
    if (varName == "passageID" || varName == "passageIDUseVar") {
        previewWin.clearData();
    }
}
/**
 * 当属性面板中的属性修改后
 * @param cmd 对应的指令
 * @param cmdParams 对应指令的参数 
 * @param varName 修改的属性名
 * @param newValue 修改后的值
 */
function onCommandAttributeChange(cmd: Command, cmdParams: ThisCommandParams, varName: string, newValue: any) {
    // 修改了通道的话，重置所有预览显示
    if (varName == "passageID" || varName == "passageIDUseVar") {
        previewWin.setData(false);
        return;
    }
    // 使用变量作为通道的话则忽略此指令执行
    if (cmdParams.passageIDUseVar) {
        return;
    }
    // 获取通道数据
    var passageData: StandAvatarPassageData = imageLayerPassageData[cmdParams.passageID];
    if (!passageData) return;
    // 对象类别匹配且存在显示对象的话
    if (passageData.type == passageType && passageData.displayObject) {
        // 混合模式，需要转换
        if (varName == "rotation") {
            passageData.displayObject.rotation1 = newValue;
        }
        else if (varName == "expression") {
            passageData.displayObject.avatar.actionID = newValue;
        }
        else if (varName == "playType") {
            // 非播放模式下不播放
        }
        else {
            // 这些参数修改后需要刷新实际图片显示
            if (varName == "standAvatar" || varName == "refObject" || varName == "refObjectEnabled" || varName == "objectUseVar") {
                refreshStandAvatarRealShow(passageData.displayObject, cmdParams, passageData, true, true);
            }
            else {
                passageData.displayObject[varName] = newValue;
            }
        }
        // -- 刷新操作工具对象（延迟到下一帧）
        Callback.CallLaterBeforeRender(previewWin.operateObjectTool.refresh, previewWin.operateObjectTool);
    }
}
