// 图像层相关 遍历每个已使用的通道，每个已使用的通道再遍历已使用的指令，调用下面的函数
// [everyone passage] x [everyone used command type]
//------------------------------------------------------------------------------------------------------
/**
 * 清理函数：每次刷新前调用此函数进行清理
 * @param passageID 通道
 * @param passageData 通道储存的数据
 */
function imageLayerPassageClear(passageID: number, passageData: StandAvatarPassageData) {
    // -- 判断该通道是该类别
    if (passageData.type == passageType) {
        // 卸载创建的显示对象
        var a: UIStandAvatar = passageData.displayObject;
        if (a) {
            a.dispose();
        }
    }
}
/**
 * 开始函数
 * @param passageID 通道
 * @param passageData 通道储存的数据
 */
function imageLayerPassageStart(passageID: number, passageData: StandAvatarPassageData) {
    // 不是来自显示图片的话忽略掉（比如只有移动图片指令实际生效）
    if (!passageData.displayObjectFromShow) {
        return;
    }
    // 通道类别匹配的话
    if (passageData.type == passageType) {
        // -- 创建图片
        var a = new UIStandAvatar();
        a.avatar.syncLoadWhenAssetExist = true;
        a.data = passageData;
        passageData.displayObject = a;
        // -- 同Z轴时按该优先级出现，即编号越大yue
        a.dpDisplayPriority = passageID;
        // a.image = passageData.image;
        refreshStandAvatarRealShow(passageData.displayObject, null, passageData, false, false);

        imageLayer.addChild(a);
        // -- 深度坐标
        a.useDPCoordScaleMode = true;
        a.useDPCoord = true;
        a.dpX = passageData.dpX;
        a.dpY = passageData.dpY;
        a.dpZ = passageData.dpZ;
        a.dpScaleX = passageData.dpScaleX;
        a.dpScaleY = passageData.dpScaleY;
        a.rotation1 = passageData.rotation;
        a.opacity = passageData.opacity;
        a.playOnce = false;
        a.avatarFPS = passageData.avatarFPS;
        a.avatarFrame = passageData.avatarFrame;
        a.avatar.actionID = passageData.expression;
        a.isPlay = false;

        // -- 如果是当前指令的话则选中它
        if (previewWin.currentSelectCmd == passageData.cmd) {
            Callback.CallLaterBeforeRender(() => {
                previewWin.operateObjectTool.selectObjects([a]);
            }, this)
        }
        else if (!previewWin.allowAllDisplayObject) {
            a.mouseEnabled = false;
            a.setTonal(-200, -200, -200, 0);
        }
        previewWin.operateObjectTool.addExcludeOperactionObject(a);
        // ---
        a.on(EventObject.RIGHT_MOUSE_UP, this, (a: UIBitmap) => {
            if (previewWin.viewAreaRightMouseMove) return;
            var menuData: any[] = [
                {
                    label: "定位到对应的事件",
                    splitLine: true,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "selectEvent", // 点击后执行的方法名称
                    shortcutKey: "F", //显示的快捷键名称
                    menuHotKey: "F", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                },
                {
                    label: "生成「显示立绘事件」",
                    splitLine: false,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "createShowStandAvatarEvent", // 点击后执行的方法名称
                    shortcutKey: "", //显示的快捷键名称
                    menuHotKey: "", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                },
                {
                    label: "生成「移动立绘事件」",
                    splitLine: false,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "createMoveStandAvatarEvent", // 点击后执行的方法名称
                    shortcutKey: "", //显示的快捷键名称
                    menuHotKey: "", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                },
                {
                    label: "生成「消除立绘事件」",
                    splitLine: false,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "earseEvent", // 点击后执行的方法名称
                    shortcutKey: "D", //显示的快捷键名称
                    menuHotKey: "D", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                }
            ];
            // 关于生成的指令是插入在上方还是下方
            var isinsertCMDToUp = passageData.cmd == previewWin.currentSelectCmd ? false : true;
            var funcDomain = {
                selectEvent: () => {
                    previewWin.selectCmdMessageByCmd(passageData.cmd, true);
                },
                createShowStandAvatarEvent: () => {
                    var d = a.data as StandAvatarPassageData;
                    createAnimationMoveData(a, d);
                    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_showStandAvatar, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                    newCmdData.cmd = newCMD;
                    newCmdParams = null;
                    previewWin.setData();
                },
                createMoveStandAvatarEvent: () => {
                    var d = a.data as StandAvatarPassageData;
                    createAnimationMoveData(a, d);
                    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_moveStandAvatar, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                    newCmdData.cmd = newCMD;
                    newCmdParams = null;
                    previewWin.setData();
                },
                earseEvent: () => {
                    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_earsePicture, {
                        passageIDUseVar: passageData.passageIDUseVar,
                        passageID: passageData.passageID
                    }, isinsertCMDToUp ? -2 : -3, false);
                    previewWin.setData();
                }
            };
            previewWin.createMenu(menuData, stage.mouseX, stage.mouseY, funcDomain);
        }, [a]);
    }
}
//------------------------------------------------------------------------------------------------------
// 自定义
//------------------------------------------------------------------------------------------------------
function createAnimationMoveData(sp: GameSprite, data: StandAvatarPassageData) {
    newCmdParams = ObjectUtils.depthClone(data.lastStandAvatarCmdParams);
    newCmdParams.dpX = sp.dpX;
    newCmdParams.dpY = sp.dpY;
    newCmdParams.dpZ = sp.dpZ;
    newCmdParams.rotation = sp.rotation1;
    newCmdParams.dpScaleX = sp.dpScaleX;
    newCmdParams.dpScaleY = sp.dpScaleY;
    newCmdParams.passageID = data.passageID;
    newCmdData = data;
}
/**
 * 当来自操作工具的属性修改时
 * @param type 类别 0-移动 1-缩放 2-旋转  
 * @param realSelObjs 选中的工具
 */
function imageLayerOnToolsChange(type: number, realSelObjs: GameSprite[]) {
    // 根据选中的数据进行获取
    for (var i = 0; i < realSelObjs.length; i++) {
        var sp = realSelObjs[i];
        var data: StandAvatarPassageData = sp.data;
        // 如果存在数据且数据类别是当前指令的类别时
        if (data && data.type == passageType && data.cmd.customID == commandID) {
            // 实际坐标转化为深度坐标
            var res = sp.realCoordToDPCoord(type == 0, type == 1);
            // 选中的指令是当前指令时：则直接修改属性
            if (previewWin.currentSelectCmd == data.cmd) {
                var passageID: number = data.passageID as any;
                if (type == 0) {
                    previewWin.modifyCommandParam(data.cmd, "dpX", sp.dpX);
                    previewWin.modifyCommandParam(data.cmd, "dpY", sp.dpY);
                }
                else if (type == 1) {
                    previewWin.modifyCommandParam(data.cmd, "dpScaleX", res.dpScaleX);
                    previewWin.modifyCommandParam(data.cmd, "dpScaleY", res.dpScaleY);
                }
                else {
                    previewWin.modifyCommandParam(data.cmd, "rotation", sp.rotation);
                }
            }
            else {
                // 不存在数据时则创建
                if (newCmdParams == null) {
                    createAnimationMoveData(sp, data);
                }
                // 否则直接修改
                else {
                    if (type == 0) {
                        newCmdParams.dpX = sp.dpX;
                        newCmdParams.dpY = sp.dpY;
                    }
                    else if (type == 1) {
                        newCmdParams.dpScaleX = res.dpScaleX;
                        newCmdParams.dpScaleY = res.dpScaleY;
                    }
                    else {
                        newCmdParams.rotation = sp.rotation;
                    }
                }
            }
        }
    }
}
/**
 * 刷新图片实际的显示
 * @param uiBitmap 图片对象
 * @param cmdParams 参数（可能为空）
 * @param passageData 通道数据
 * @param needSyncCMDParams [可选] 默认值=false 是否同步指令参数
 * @param needResizeWhenEmptyImage [可选] 默认值=false 是否重置尺寸当空图片时
 */
function refreshStandAvatarRealShow(displayObject: UIStandAvatar, cmdParams: ThisCommandParams, passageData: StandAvatarPassageData, needSyncCMDParams: boolean = false, needResizeWhenEmptyImage: boolean = false) {
    var toAvatar: number;
    var paramData = cmdParams ? cmdParams : passageData;
    // 使用变量的情况下
    if (paramData.objectUseVar) {
        if (paramData.refObjectEnabled) {
            toAvatar = paramData.refObject ? paramData.refObject : 0;
        }
        else {
            toAvatar = 0;
        }
    }
    // 普通图片显示
    else {
        toAvatar = paramData.standAvatar ? paramData.standAvatar : 0;
    }
    // 原图是空图片的话允许重置为新图的尺寸
    // if (needResizeWhenEmptyImage && uiBitmap.image == emptyImageURL) {
    //     passageData.uiBitmap["_emptyImage"] = true;
    //     if (needSyncCMDParams) {
    //         passageData.uiBitmap.once(EventObject.LOADED, this, (passageData: ImagePassageData) => {
    //             previewWin.modifyCommandParam(passageData.cmd, "dpWidth", passageData.uiBitmap.dpWidth);
    //             previewWin.modifyCommandParam(passageData.cmd, "dpHeight", passageData.uiBitmap.dpHeight);

    //         }, [passageData]);
    //     }
    // }
    displayObject.avatarID = toAvatar;
}

3006.cmdPreviewJS:
var passageType = "gcStandAvatar";
var PLUGIN_CUSTOM_COMMAND_showStandAvatar = 3006;
var PLUGIN_CUSTOM_COMMAND_moveStandAvatar = 3007;
var PLUGIN_CUSTOM_COMMAND_earsePicture = 3008;
var newCmdParams;
var newCmdData;
function runCommand(cmdParams, cmd, isPlayMode) {
    if (cmdParams.passageIDUseVar) {
        return;
    }
    var passageData = imageLayerPassageData[cmdParams.passageID];
    if (isPlayMode) {
        if (passageData) {
            passageData.onCoverPassage && passageData.onCoverPassage.apply(this, [cmdParams.passageID, passageData]);
        }
    }
    passageData = imageLayerPassageData[cmdParams.passageID] = {};
    passageData.passageID = cmdParams.passageID;
    passageData.type = passageType;
    passageData.cmd = cmd;
    passageData.lastStandAvatarCmdParams = cmdParams;
    passageData.objectUseVar = cmdParams.objectUseVar;
    passageData.refObjectEnabled = cmdParams.refObjectEnabled;
    passageData.refObject = cmdParams.refObject;
    if (cmdParams.objectUseVar) {
        if (cmdParams.refObjectEnabled) {
            passageData.standAvatar = cmdParams.refObject ? cmdParams.refObject : 0;
        }
        else {
            passageData.standAvatar = 0;
        }
    }
    else {
        passageData.standAvatar = cmdParams.standAvatar ? cmdParams.standAvatar : 0;
    }
    passageData.dpX = cmdParams.dpX;
    passageData.dpY = cmdParams.dpY;
    passageData.dpZ = cmdParams.dpZ;
    passageData.dpScaleX = cmdParams.dpScaleX;
    passageData.dpScaleY = cmdParams.dpScaleY;
    passageData.rotation = cmdParams.rotation;
    passageData.opacity = cmdParams.opacity;
    passageData.displayObjectFromShow = true;
    passageData.playType = cmdParams.playType;
    passageData.avatarFPS = cmdParams.avatarFPS;
    passageData.avatarFrame = cmdParams.avatarFrame;
    passageData.expression = cmdParams.expression ? cmdParams.expression : 0;
    if (isPlayMode) {
        passageData.onCoverPassage = imageLayerPassageClear;
        var a = new UIStandAvatar();
        a.avatar.syncLoadWhenAssetExist = true;
        a.useDPCoordScaleMode = true;
        a.dpDisplayPriority = passageData.passageID;
        a.avatarID = passageData.standAvatar;
        a.data = passageData;
        imageLayer.addChild(a);
        a.useDPCoord = true;
        a.dpX = passageData.dpX;
        a.dpY = passageData.dpY;
        a.dpZ = passageData.dpZ;
        a.dpScaleX = passageData.dpScaleX;
        a.dpScaleY = passageData.dpScaleY;
        a.rotation1 = passageData.rotation;
        a.opacity = passageData.opacity;
        a.playOnce = passageData.playType == 1;
        a.avatarFPS = passageData.avatarFPS;
        a.avatarFrame = passageData.avatarFrame;
        a.avatar.actionID = passageData.expression;
        a.isPlay = passageData.playType != 0;
        a.dpCoordToRealCoord();
        passageData.displayObject = a;
    }
}
function inEditorPlayPreload(onFin, cmdParams) {
    var toAvatar;
    if (cmdParams.objectUseVar) {
        if (cmdParams.refObjectEnabled) {
            toAvatar = cmdParams.refObject ? cmdParams.refObject : 0;
        }
        else {
            toAvatar = 0;
        }
    }
    else {
        toAvatar = cmdParams.standAvatar ? cmdParams.standAvatar : 0;
    }
    AssetManager.preLoadStandAvatarAsset(toAvatar, Callback.New(onFin, this), true);
}
function onCommandAttributeBeforeChange(cmd, cmdParams, varName) {
    if (varName == "passageID" || varName == "passageIDUseVar") {
        previewWin.clearData();
    }
}
function onCommandAttributeChange(cmd, cmdParams, varName, newValue) {
    if (varName == "passageID" || varName == "passageIDUseVar") {
        previewWin.setData(false);
        return;
    }
    if (cmdParams.passageIDUseVar) {
        return;
    }
    var passageData = imageLayerPassageData[cmdParams.passageID];
    if (!passageData)
        return;
    if (passageData.type == passageType && passageData.displayObject) {
        if (varName == "rotation") {
            passageData.displayObject.rotation1 = newValue;
        }
        else if (varName == "expression") {
            passageData.displayObject.avatar.actionID = newValue;
        }
        else if (varName == "playType") {
        }
        else {
            if (varName == "standAvatar" || varName == "refObject" || varName == "refObjectEnabled" || varName == "objectUseVar") {
                refreshStandAvatarRealShow(passageData.displayObject, cmdParams, passageData, true, true);
            }
            else {
                passageData.displayObject[varName] = newValue;
            }
        }
        Callback.CallLaterBeforeRender(previewWin.operateObjectTool.refresh, previewWin.operateObjectTool);
    }
}
function imageLayerOnToolsChangePostion(realSelObjs) {
    imageLayerOnToolsChange(0, realSelObjs);
}
function imageLayerOnToolsChangeSize(realSelObjs) {
    imageLayerOnToolsChange(1, realSelObjs);
}
function imageLayerOnToolsChangeRotation(realSelObjs) {
    imageLayerOnToolsChange(2, realSelObjs);
}
function imageLayerOnToolsChangeStart(realSelObjs) {
}
function imageLayerOnToolsChangeEnd(realSelObjs) {
    if (newCmdParams == null)
        return;
    if (realSelObjs.length == 0)
        return;
    var data = realSelObjs[0].data;
    if (!data || data.type != passageType)
        return;
    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_moveStandAvatar, newCmdParams, -2, false);
    newCmdData.cmd = newCMD;
    newCmdParams = null;
    previewWin.setData();
}
function imageLayerOnToolsSelectObject(realSelObjs) {
}
function imageLayerPassageClear(passageID, passageData) {
    if (passageData.type == passageType) {
        var a = passageData.displayObject;
        if (a) {
            a.dispose();
        }
    }
}
function imageLayerPassageStart(passageID, passageData) {
    if (!passageData.displayObjectFromShow) {
        return;
    }
    if (passageData.type == passageType) {
        var a = new UIStandAvatar();
        a.avatar.syncLoadWhenAssetExist = true;
        a.data = passageData;
        passageData.displayObject = a;
        a.dpDisplayPriority = passageID;
        refreshStandAvatarRealShow(passageData.displayObject, null, passageData, false, false);
        imageLayer.addChild(a);
        a.useDPCoordScaleMode = true;
        a.useDPCoord = true;
        a.dpX = passageData.dpX;
        a.dpY = passageData.dpY;
        a.dpZ = passageData.dpZ;
        a.dpScaleX = passageData.dpScaleX;
        a.dpScaleY = passageData.dpScaleY;
        a.rotation1 = passageData.rotation;
        a.opacity = passageData.opacity;
        a.playOnce = false;
        a.avatarFPS = passageData.avatarFPS;
        a.avatarFrame = passageData.avatarFrame;
        a.avatar.actionID = passageData.expression;
        a.isPlay = false;
        if (previewWin.currentSelectCmd == passageData.cmd) {
            Callback.CallLaterBeforeRender(function () {
                previewWin.operateObjectTool.selectObjects([a]);
            }, this);
        }
        else if (!previewWin.allowAllDisplayObject) {
            a.mouseEnabled = false;
            a.setTonal(-200, -200, -200, 0);
        }
        previewWin.operateObjectTool.addExcludeOperactionObject(a);
        a.on(EventObject.RIGHT_MOUSE_UP, this, function (a) {
            if (previewWin.viewAreaRightMouseMove)
                return;
            var menuData = [
                {
                    label: "定位到对应的事件",
                    splitLine: true,
                    children: [],
                    execute: "selectEvent",
                    shortcutKey: "F",
                    menuHotKey: "F",
                    enabled: true,
                    visible: true,
                },
                {
                    label: "生成「显示立绘事件」",
                    splitLine: false,
                    children: [],
                    execute: "createShowStandAvatarEvent",
                    shortcutKey: "",
                    menuHotKey: "",
                    enabled: true,
                    visible: true,
                },
                {
                    label: "生成「移动立绘事件」",
                    splitLine: false,
                    children: [],
                    execute: "createMoveStandAvatarEvent",
                    shortcutKey: "",
                    menuHotKey: "",
                    enabled: true,
                    visible: true,
                },
                {
                    label: "生成「消除立绘事件」",
                    splitLine: false,
                    children: [],
                    execute: "earseEvent",
                    shortcutKey: "D",
                    menuHotKey: "D",
                    enabled: true,
                    visible: true,
                }
            ];
            var isinsertCMDToUp = passageData.cmd == previewWin.currentSelectCmd ? false : true;
            var funcDomain = {
                selectEvent: function () {
                    previewWin.selectCmdMessageByCmd(passageData.cmd, true);
                },
                createShowStandAvatarEvent: function () {
                    var d = a.data;
                    createAnimationMoveData(a, d);
                    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_showStandAvatar, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                    newCmdData.cmd = newCMD;
                    newCmdParams = null;
                    previewWin.setData();
                },
                createMoveStandAvatarEvent: function () {
                    var d = a.data;
                    createAnimationMoveData(a, d);
                    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_moveStandAvatar, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                    newCmdData.cmd = newCMD;
                    newCmdParams = null;
                    previewWin.setData();
                },
                earseEvent: function () {
                    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_earsePicture, {
                        passageIDUseVar: passageData.passageIDUseVar,
                        passageID: passageData.passageID
                    }, isinsertCMDToUp ? -2 : -3, false);
                    previewWin.setData();
                }
            };
            previewWin.createMenu(menuData, stage.mouseX, stage.mouseY, funcDomain);
        }, [a]);
    }
}
function createAnimationMoveData(sp, data) {
    newCmdParams = ObjectUtils.depthClone(data.lastStandAvatarCmdParams);
    newCmdParams.dpX = sp.dpX;
    newCmdParams.dpY = sp.dpY;
    newCmdParams.dpZ = sp.dpZ;
    newCmdParams.rotation = sp.rotation1;
    newCmdParams.dpScaleX = sp.dpScaleX;
    newCmdParams.dpScaleY = sp.dpScaleY;
    newCmdParams.passageID = data.passageID;
    newCmdData = data;
}
function imageLayerOnToolsChange(type, realSelObjs) {
    for (var i = 0; i < realSelObjs.length; i++) {
        var sp = realSelObjs[i];
        var data = sp.data;
        if (data && data.type == passageType && data.cmd.customID == commandID) {
            var res = sp.realCoordToDPCoord(type == 0, type == 1);
            if (previewWin.currentSelectCmd == data.cmd) {
                var passageID = data.passageID;
                if (type == 0) {
                    previewWin.modifyCommandParam(data.cmd, "dpX", sp.dpX);
                    previewWin.modifyCommandParam(data.cmd, "dpY", sp.dpY);
                }
                else if (type == 1) {
                    previewWin.modifyCommandParam(data.cmd, "dpScaleX", res.dpScaleX);
                    previewWin.modifyCommandParam(data.cmd, "dpScaleY", res.dpScaleY);
                }
                else {
                    previewWin.modifyCommandParam(data.cmd, "rotation", sp.rotation);
                }
            }
            else {
                if (newCmdParams == null) {
                    createAnimationMoveData(sp, data);
                }
                else {
                    if (type == 0) {
                        newCmdParams.dpX = sp.dpX;
                        newCmdParams.dpY = sp.dpY;
                    }
                    else if (type == 1) {
                        newCmdParams.dpScaleX = res.dpScaleX;
                        newCmdParams.dpScaleY = res.dpScaleY;
                    }
                    else {
                        newCmdParams.rotation = sp.rotation;
                    }
                }
            }
        }
    }
}
function refreshStandAvatarRealShow(displayObject, cmdParams, passageData, needSyncCMDParams, needResizeWhenEmptyImage) {
    if (needSyncCMDParams === void 0) { needSyncCMDParams = false; }
    if (needResizeWhenEmptyImage === void 0) { needResizeWhenEmptyImage = false; }
    var toAvatar;
    var paramData = cmdParams ? cmdParams : passageData;
    if (paramData.objectUseVar) {
        if (paramData.refObjectEnabled) {
            toAvatar = paramData.refObject ? paramData.refObject : 0;
        }
        else {
            toAvatar = 0;
        }
    }
    else {
        toAvatar = paramData.standAvatar ? paramData.standAvatar : 0;
    }
    displayObject.avatarID = toAvatar;
}

3006.customPhraseDescTS:
// 根据属性名称值编写自定义短语描述，如："我的短语是"+attrs.xxx
// 使用下拉框组件的变量attrs.xxx有对应的attrs.xxxItems的属性以便获得标签名集合
// 获取自定义模块的数据名称的函数：getModuleDataName(moduleID:number,dataID:number):string;
// 获取各种列表内数据名称的函数：getTypeDataListName(typeID: number, dataID: number):string;
// -- typeID ：0-全局变量 1-全局开关 2-全局字符串 3-玩家变量 4-玩家开关 5-玩家字符串
//           ：6-场景列表 7-场景对象模型列表 8-图块列表 9-行走图列表 10-行走图动作列表 11-立绘列表 12-立绘表情列表
//           ：13-公共事件 14-对话框列表 15-动画列表 16-动画信号列表 17-界面列表 18-数据结构列表 19-自定义模块列表
//           ：20-自定义命令列表 21-自定义行为列表 22-自动元件列表 23-行走图部件列表 24-立绘部件列表
var str = ``;
var passageIDStr = attrs.passageIDUseVar ? `@${attrs.passageIDVar}-${getTypeDataListName(3, attrs.passageIDVar)}` : attrs.passageID;
str += `编号【${passageIDStr}】`;
var objStr = attrs.objectUseVar ? `${attrs.standAvatarVar}-${getTypeDataListName(3, attrs.standAvatarVar)}` : `${attrs.standAvatar}-${getTypeDataListName(11, attrs.standAvatar)}`;
str += `「${objStr} `;
var expStr = attrs.expressionUseVar ? `${attrs.expressionVar}-${getTypeDataListName(3, attrs.expressionVar)}` : `${getTypeDataListName(12, attrs.expression)}`;
str += ` ${expStr}」 `;

if (attrs.posUseVar) {
    var xStr = `@${attrs.dpXVar}`;
    var yStr = `@${attrs.dpYVar}`;
}
else {
    xStr = attrs.dpX as any;
    yStr = attrs.dpY as any;
}
var zStr = attrs.zUseVar ? `@${attrs.dpZVar}` : attrs.dpZ;
if (attrs.sizeUseVar) {
    var wStr = `@${attrs.dpScaleX}`;
    var hStr = `@${attrs.dpScaleY}`;
}
else {
    wStr = (attrs.dpScaleX * 100) + "%";
    hStr = (attrs.dpScaleY * 100) + "%";
}
var roStr = attrs.rotationUseVar ? `@${attrs.rotationVar}` : attrs.rotation;
var alphaStr = attrs.opacityUseVar ? `@${attrs.opacityVar}` : attrs.opacity;
var startFrame = attrs.frameUseVar ? `@${attrs.avatarFrameVar}` : attrs.avatarFrame;
if (attrs.playType == 0) {
    str += ` 第${startFrame}帧 `;
    str += ` 坐标(${xStr},${yStr},${zStr}) 尺寸(${wStr},${hStr}) 旋转度=${roStr} 透明度=${alphaStr}`;

}
else {
    str += ` 坐标(${xStr},${yStr},${zStr}) 尺寸(${wStr},${hStr}) 旋转度=${roStr} 透明度=${alphaStr}`;
    str += ` 第${startFrame}帧 ${attrs.playTypeItems[attrs.playType]} `;
}

if (attrs.playType != 0) {
    str += ` 帧率=${attrs.avatarFPS}`;
}
str;

