    var yStr = "@" + attrs.dpYVar;
}
else {
    xStr = attrs.dpX;
    yStr = attrs.dpY;
}
var zStr = attrs.zUseVar ? "@" + attrs.dpZVar : attrs.dpZ;
if (attrs.sizeUseVar) {
    var wStr = "@" + attrs.dpScaleX;
    var hStr = "@" + attrs.dpScaleY;
}
else {
