// [everyone passage] x [everyone used command type]
//------------------------------------------------------------------------------------------------------
/**
 * 清理函数：每次刷新前调用此函数进行清理
 * @param passageID 通道
 * @param passageData 通道储存的数据
 */
function imageLayerPassageClear(passageID: number, passageData: UIPassageData) {
    // -- 判断该通道是该类别
    if (passageData.type == passageType) {
        // 卸载创建的显示对象
        var a: GUI_BASE = passageData.displayObject;
        if (a) {
            a.dispose();
        }
    }
}
/**
 * 开始函数
 * @param passageID 通道
 * @param passageData 通道储存的数据
 */
function imageLayerPassageStart(passageID: number, passageData: UIPassageData) {
    // 不是来自显示图片的话忽略掉（比如只有移动图片指令实际生效）
    if (!passageData.displayObjectFromShow) {
        return;
    }
    // 通道类别匹配的话
    if (passageData.type == passageType) {
        // -- 创建图片
        var a = GameUI.load(passageData.uiID, true);
        // a.syncLoadWhenAssetExist = true;
        a.data = passageData;
        passageData.displayObject = a;
        if (passageData.showType == 1) {
            imageLayer.addChild(a);
            // -- 同Z轴时按该优先级出现，即编号越大yue
            a.dpDisplayPriority = passageID;
            a.width = Config.WINDOW_WIDTH;
            a.height = Config.WINDOW_HEIGHT;
            // refreshUIRealShow(passageData.displayObject, null, passageData, false, false);
            // -- 深度坐标
            a.useDPCoordScaleMode = true;
            a.useDPCoord = true;
            if (passageData.setAttr) {
                a.dpX = passageData.dpX;
                a.dpY = passageData.dpY;
                a.dpZ = passageData.dpZ;
                a.dpScaleX = passageData.dpScaleX;
                a.dpScaleY = passageData.dpScaleY;
                a.rotation1 = passageData.rotation;
                a.opacity = passageData.opacity;
            }
        }
        else {
            uiLayer.addChild(a);
            a.dpDisplayPriority = passageData.dpDisplayPriority;
            if (passageData.setAttr) {
                a.x = passageData.dpX;
                a.y = passageData.dpY;
                a.scaleX = passageData.dpScaleX;
                a.scaleY = passageData.dpScaleY;
                a.width = Config.WINDOW_WIDTH;
                a.height = Config.WINDOW_HEIGHT;
                a.rotation = passageData.rotation;
                a.opacity = passageData.opacity;
            }
        }
        // -- 如果是当前指令的话则选中它
        if (previewWin.currentSelectCmd == passageData.cmd) {
            Callback.CallLaterBeforeRender(() => {
                previewWin.operateObjectTool.selectObjects([a]);
            }, this)
        }
        else if (!previewWin.allowAllDisplayObject) {
                a.mouseEnabled = false;
                a.setTonal(-200, -200, -200, 0);
        }
        previewWin.operateObjectTool.addExcludeOperactionObject(a);
        // ---
        a.on(EventObject.RIGHT_MOUSE_UP, this, (a: UIBitmap) => {
            if (previewWin.viewAreaRightMouseMove) return;
            var menuData: any[] = [
                {
                    label: "定位到对应的事件",
                    splitLine: true,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "selectEvent", // 点击后执行的方法名称
                    shortcutKey: "F", //显示的快捷键名称
                    menuHotKey: "F", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                },
                {
                    label: "生成「显示界面事件」",
                    splitLine: false,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "createShowUIEvent", // 点击后执行的方法名称
                    shortcutKey: "", //显示的快捷键名称
                    menuHotKey: "", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                },
                {
                    label: "生成「移动界面事件」",
                    splitLine: false,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "createMoveUIEvent", // 点击后执行的方法名称
                    shortcutKey: "", //显示的快捷键名称
                    menuHotKey: "", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                },
                {
                    label: "生成「关闭界面事件」",
                    splitLine: false,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "earseEvent", // 点击后执行的方法名称
                    shortcutKey: "D", //显示的快捷键名称
                    menuHotKey: "D", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                }
            ];
            // 关于生成的指令是插入在上方还是下方
            var isinsertCMDToUp = passageData.cmd == previewWin.currentSelectCmd ? false : true;
            var funcDomain = {
                selectEvent: () => {
                    previewWin.selectCmdMessageByCmd(passageData.cmd, true);
                },
                createShowUIEvent: () => {
                    var d = a.data as UIPassageData;
                    createUIMoveData(a, d);
                    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_showUI, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                    newCmdData.cmd = newCMD;
                    newCmdParams = null;
                    previewWin.setData();
                },
                createMoveUIEvent: () => {
                    var d = a.data as UIPassageData;
                    createUIMoveData(a, d);
                    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_moveUI, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                    newCmdData.cmd = newCMD;
                    newCmdParams = null;
                    previewWin.setData();
                },
                earseEvent: () => {
                    // 如果是使用图像层的话则关闭它，否则关闭界面
                    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_closeUI, {
                        showType: passageData.showType,
                        passageID: passageData.passageID,
                        uiID: passageData.uiID
                    }, isinsertCMDToUp ? -2 : -3, false);
                    previewWin.setData();
                }
            };
            previewWin.createMenu(menuData, stage.mouseX, stage.mouseY, funcDomain);
        }, [a]);

    }
}
