3011.cmdPreviewTS:
//------------------------------------------------------------------------------------------------------
// 编辑器预览窗口运行时提供的系统变量
// 关于预览的流程：
// -- 1.imageLayerPassageClear 每已使用的通道x每已使用的指令
// -- 2.runCommand 每个指令按顺序执行一遍
// -- 3.imageLayerPassageStart 每已使用的通道x每已使用的指令
// 关于播放的流程
// -- 1.imageLayerPassageClear 每已使用的通道x每已使用的指令
// -- 2.runCommand 每个指令按顺序播放
//------------------------------------------------------------------------------------------------------
/**
 * 当前自定义指令的编号
 */
declare var commandID: number;
/**
 * 图像层通道数据
 */
declare var imageLayerPassageData: any[];
/**
 * 图像层显示对象容器
 */
declare var imageLayer: GameImageLayer;
/**
 * 界面层显示对象容器
 */
declare var uiLayer: GameSprite;
/**
 * 预览窗口
 */
declare var previewWin: {
    /**
     * 清理全部事件指令产生的预览效果
     */
    clearData: Function;
    /**
     * 设置数据，刷新预览效果直到当前选中的指令行
     */
    setData: Function;
    /**
     * 当前选中的指令
     */
    currentSelectCmd: Command;
    /**
     * 视图区域是否右键移动过
     */
    viewAreaRightMouseMove: boolean;
    /**
     * 界面层显示优先级计数 从0开始
     */
    uiLayerDisplayPriority: number;
    /**
     * 允许显示全部对象
     */
    allowAllDisplayObject: boolean;
    /**
     * 对象操作工具
     */
    operateObjectTool: {
        // 刷新对象操作工具，比如来自其他地方更改了对象的位置或尺寸等，可使用该函数刷新
        refresh: Function;
        // 选中对象
        selectObjects: (sp: GameSprite[]) => void;
        // 需要排除缩放和旋转工具的对象
        addExcludeOperactionObject: (sp: GameSprite) => void
    }
    /**
     * 选中事件指令
     * @param cmd 事件指令
     * @param sendEvent 派发事件 默认=false 派发事件会刷新属性栏、预览窗口等
     */
    selectCmdMessageByCmd(cmd: Command, sendEvent?: boolean);
    /**
     * 修改指令属性
     * @param cmd 需要修改的指令
     * @param varName 参数变量名称
     * @param newValue 新的值
     */
    modifyCommandParam: (cmd: Command, varName: string, newValue: any) => void;
    /**
     * 新增指令 默认是默认值，如果有写入参数的话则以参数为准 varNameValue
     * @param customID 自定义ID
     * @param varNameValue 额外的变量值，如存在则优先使用该值，否则使用自定义编辑器中的默认值，如 {dpX:100,dpY:200}
     * @param selectedIndex [可选] 默认值=-2  -3=当前选中（之后） -2=当前选中（之前） -1=最尾端 0-N=自定义 
     * @param refreshPreViewArea [可选] 默认值=true 刷新预览区
     */
    insertCustomCMD(customID: number, varNameValue: any, selectedIndex?: number, refreshPreViewArea?: boolean): Command;
    /**
     * 更改显示层
     * @param selectedIndex 0-界面层 1-图像层
     */
    changeLayer(selectedIndex: number): void;
    /**
     * 创建菜单
     * var menuData:any[] = [
               {
                   label:"重命名",
                   splitLine:false,// 是否显示分割线
                   children:[], // 存放和本身这个对象是一样的格式
                   execute:"functionName", // 点击后执行的方法名称
                   shortcutKey:"", //显示的快捷键名称
                   menuHotKey:"", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                   enabled:true, // 是否可用，不填写表示true
                   visible:true, // 是否显示，不填写表示true
				   selected:true, // 如果存在则以打钩的形式选择此项
				   group:"myGroupName" // 配合selected，如若存在则同组单选
               }
           ];
     * @param menuData 菜单数据
     * @param x 全局坐标X
     * @param y 全局坐标Y
     * @param executeDomain 函数作用域
     */
    createMenu(menuData: any[], x: number, y: number, executeDomain: any);
}
//------------------------------------------------------------------------------------------------------
// 通道数据
//------------------------------------------------------------------------------------------------------
declare class UIPassageData {
    // ====> system 必要项
    /**
     * 通道编号
     */
    passageID: number;
    /**
     * 类别，一般用于区分显示对象，如图片、动画、立绘、界面都是属于不同的类别
     */
    type: string;
    /**
     * 记录关联的指令
     */
    cmd: Command;
    /**
     * 当通道覆盖时调用的函数，以便清理掉。如显示了2号图片或移动了号图片，再次显示2号图片时会清理掉此前显示的图片或移动行为
     */
    onCoverPassage: Function;

    // ====> custom 自定义属性
    dpDisplayPriority: number; // 显示层次优先级
    passageIDUseVar: boolean;
    timeFunc: Function;
    uiID: number;
    displayObject: GUI_BASE;
    displayObjectFromShow: boolean; // 区分显示对象来源，是否来自显示图片，否则来自移动图片
    dpX: number;
    dpY: number;
    dpZ: number;
    dpScaleX: number;
    dpScaleY: number;
    rotation: number;
    opacity: number;
    setAttr: boolean;
    showType: number;
    objectUseVar: boolean;
    refObjectEnabled: boolean;
    refObject: number;
    lastStandAvatarCmdParams: ThisCommandParams; // 记录上一个关联的指令的参数
}
//------------------------------------------------------------------------------------------------------
// 自定义变量
//------------------------------------------------------------------------------------------------------
var passageType = "gcUI";
var PLUGIN_CUSTOM_COMMAND_showUI: number = 3010;
var PLUGIN_CUSTOM_COMMAND_moveUI: number = 3011;
var PLUGIN_CUSTOM_COMMAND_closeUI: number = 3012;
var newCmdParams;
var newCmdData: UIPassageData;
//------------------------------------------------------------------------------------------------------
// [everyone used command] 逐指令行目执行
//------------------------------------------------------------------------------------------------------
/**
 * 逐指令执行：对应事件下按照从上至下的顺序逐行执行，如果是该自定义指令时则会执行该函数
 * 该函数用于编辑器预览和播放，如果分歧面板中筛选不经过的事件指令则不会执行该函数。
 * 比如事件页中有3个该指令，则按照顺序执行3次该函数
 * @param cmdParams 指令的自定义参数数据，根据用户设定值
 * @param cmd 对应的指令对象
 * @param isPlayMode 是否是播放模式，否则是普通的预览模式
 */
function runCommand(cmdParams: ThisCommandParams, cmd: Command, isPlayMode: boolean) {
    // -- 图像层的场合
    if (cmdParams.showType == 1) {
        var passageID = cmdParams.passageID;
        // 使用变量作为通道的话则忽略此指令执行
        if (cmdParams.passageIDUseVar) {
            return;
        }
    }
    // -- 界面层的场合
    else {
        passageID = 1000000 + cmdParams.uiID;
    }
    // -- 获取通道数据
    var passageData: UIPassageData = imageLayerPassageData[passageID];
    // 播放模式的情况
    if (isPlayMode) {
        // 没有该通道或通道储存类型不匹配或无显示对象的话则忽略掉
        if (!passageData || passageData.type != passageType || !passageData.displayObject) return;
        // 清理掉正在执行的移动
        if (passageData.timeFunc) {
            os.remove_ENTERFRAME(passageData.timeFunc, this);
        }

        var toDpX = cmdParams.dpX;
        var toDpY = cmdParams.dpY;
        var toDpZ = cmdParams.dpZ;
        var toDpScaleX = cmdParams.dpScaleX;
        var toDpScaleY = cmdParams.dpScaleY;
        var toRotation = cmdParams.rotation;
        var toOpacity = cmdParams.opacity;

        // 立即模式：无需清理此行为
        if (cmdParams.timeType == 0) {
            if (cmdParams.showType == 1) {
                passageData.displayObject.dpX = toDpX;
                passageData.displayObject.dpY = toDpY;
                passageData.displayObject.dpZ = toDpZ;
                passageData.displayObject.dpScaleX = toDpScaleX;
                passageData.displayObject.dpScaleY = toDpScaleY;
                passageData.displayObject.dpCoordToRealCoord();
            }
            else {
                passageData.displayObject.x = toDpX;
                passageData.displayObject.y = toDpY;
                passageData.displayObject.scaleX = toDpScaleX;
                passageData.displayObject.scaleY = toDpScaleY;
            }
            passageData.displayObject.rotation1 = toRotation;
            passageData.displayObject.opacity = toOpacity;
        }
        // 过渡模式：由于注册了帧刷，需要清理
        else {
            // 下一帧开始移动 1/MAX
            // GameUtils.getValueByTransData(null, );
            if (cmdParams.showType == 1) {
                var cX = passageData.displayObject.dpX;
                var cY = passageData.displayObject.dpY;
                var cScaleX = passageData.displayObject.dpScaleX;
                var cScaleY = passageData.displayObject.dpScaleY;
            }
            else {
                var cX = passageData.displayObject.x;
                var cY = passageData.displayObject.y;
                var cScaleX = passageData.displayObject.scaleX;
                var cScaleY = passageData.displayObject.scaleY;
            }

            var m = {
                time: cmdParams.time,
                curTime: 1,
                x: cX,
                y: cY,
                z: passageData.displayObject.dpZ,
                scaleX: cScaleX,
                scaleY: cScaleY,
                rotation: passageData.displayObject.rotation1,
                opacity: passageData.displayObject.opacity,

                x2: toDpX - cX,
                y2: toDpY - cY,
                z2: toDpZ - passageData.displayObject.dpZ,
                scaleX2: toDpScaleX - cScaleX,
                scaleY2: toDpScaleY - cScaleY,
                rotation2: toRotation - passageData.displayObject.rotation1,
                opacity2: toOpacity - passageData.displayObject.opacity,

                transData: GameUtils.getTransData(cmdParams.trans)
            }
            var f: Function;
            os.add_ENTERFRAME(f = passageData.timeFunc = (displayObject: UIStandAvatar, cmdParams: ThisCommandParams) => {
                // 立即变更
                var per = m.curTime / m.time;
                var value = GameUtils.getValueByTransData(m.transData, per);
                if (cmdParams.showType == 1) {
                    displayObject.dpX = m.x2 * value + m.x;
                    displayObject.dpY = m.y2 * value + m.y;
                    displayObject.dpZ = m.z2 * value + m.z;
                    displayObject.dpScaleX = m.scaleX2 * value + m.scaleX;
                    displayObject.dpScaleY = m.scaleY2 * value + m.scaleY;
                    displayObject.dpCoordToRealCoord();
                }
                else {
                    displayObject.x = m.x2 * value + m.x;
                    displayObject.y = m.y2 * value + m.y;
                    displayObject.scaleX = m.scaleX2 * value + m.scaleX;
                    displayObject.scaleY = m.scaleY2 * value + m.scaleY;
                }
                displayObject.rotation1 = m.rotation2 * value + m.rotation;
                displayObject.opacity = m.opacity2 * value + m.opacity;

                m.curTime++;
                if (per == 1) {
                    os.remove_ENTERFRAME(arguments.callee, this);
                }
            }, this, [passageData.displayObject, cmdParams]);
            f.apply(this, [passageData.displayObject, cmdParams]);

            // 覆盖函数
            var lastOnCoverPassage = passageData.onCoverPassage;
            passageData.onCoverPassage = function () {
                os.remove_ENTERFRAME(f, this);
                lastOnCoverPassage.apply(this, arguments);
            }
        }
    }
    else {
        // 存在通道但不是该类别的话则忽略掉
        if (passageData && passageData.type != passageType) return;
        if (!passageData) {
            passageData = imageLayerPassageData[passageID] = {} as any;
            if (cmdParams.showType == 0) {
                passageData.dpDisplayPriority = previewWin.uiLayerDisplayPriority++;
            }
        }




        // 写入数据
        passageData.passageID = passageID;
        passageData.type = passageType;
        passageData.cmd = cmd;
        if (!passageData.lastStandAvatarCmdParams) {
            passageData.lastStandAvatarCmdParams = cmdParams;
        }
        else {
            ObjectUtils.clone(cmdParams, passageData.lastStandAvatarCmdParams);
        }
        // 属性
        passageData.dpX = cmdParams.dpX;
        passageData.dpY = cmdParams.dpY;
        passageData.dpZ = cmdParams.dpZ;
        passageData.dpScaleX = cmdParams.dpScaleX;
        passageData.dpScaleY = cmdParams.dpScaleY;
        passageData.rotation = cmdParams.rotation;
        passageData.opacity = cmdParams.opacity;
        passageData.showType = cmdParams.showType;
        passageData.objectUseVar = cmdParams.objectUseVar;
        passageData.setAttr = true;
        // 来自非显示图片的话
        if (!passageData.displayObjectFromShow) {
            if (!passageData.uiID && !cmdParams.objectUseVar) passageData.uiID = cmdParams.uiID;
            if (cmdParams.refObjectEnabled) {
                passageData.refObjectEnabled = cmdParams.refObjectEnabled;
                passageData.refObject = cmdParams.refObject;
            }
        }
    }
}
//------------------------------------------------------------------------------------------------------
// 属性面板修改：当修改该指令的属性时，只会执行该指令对应的「指令源」的这些函数
// 如A指令来自自定义指令32号，每次通过属性面板快速修改A指令的参数b时，则会执行32号指令的下面2个函数
// [current used command] 
//------------------------------------------------------------------------------------------------------
/**
 * 当属性面板中的属性修改前
 * @param cmd 对应的指令
 * @param cmdParams 对应指令的参数 
 * @param varName 修改的属性名
 */
function onCommandAttributeBeforeChange(cmd: Command, cmdParams: ThisCommandParams, varName: string) {
    // 修改了通道的话，清理
    if (varName == "passageID" || varName == "passageIDUseVar" || varName == "uiID" || varName == "refObject" || varName == "refObjectEnabled" || varName == "objectUseVar" || varName == "showType") {
        previewWin.clearData();
    }
}
/**
 * 当属性面板中的属性修改后
 * @param cmd 对应的指令
 * @param cmdParams 对应指令的参数 
 * @param varName 修改的属性名
 * @param newValue 修改后的值
 */
function onCommandAttributeChange(cmd: Command, cmdParams: ThisCommandParams, varName: string, newValue: any) {
    // 修改了通道的话，重置所有预览显示
    if (varName == "passageID" || varName == "passageIDUseVar" || varName == "uiID" || varName == "refObject" || varName == "refObjectEnabled" || varName == "objectUseVar" || varName == "showType") {
        previewWin.setData(false);
        return;
    }
    // -- 图像层的场合
    if (cmdParams.showType == 1) {
        var passageID = cmdParams.passageID;
        // 使用变量作为通道的话则忽略此指令执行
        if (cmdParams.passageIDUseVar) {
            return;
        }
    }
    // -- 界面层的场合
    else {
        passageID = 1000000 + cmdParams.uiID;
    }
    // 获取通道数据
    var passageData: UIPassageData = imageLayerPassageData[passageID];
    if (!passageData) return;

    // 对象类别匹配且存在显示对象的话
    if (passageData.type == passageType && passageData.displayObject) {
        // 混合模式，需要转换
        if (varName == "rotation") {
            passageData.displayObject.rotation1 = newValue;
        }
        else {
            // 界面层的情况
            if (cmdParams.showType == 0) {
                var attrNameMapping = { dpX: "x", dpY: "y", dpScaleX: "scaleX", dpScaleY: "scaleY" };
                if (attrNameMapping[varName]) passageData.displayObject[attrNameMapping[varName]] = newValue;
                else passageData.displayObject[varName] = newValue;
            }
            else {
                passageData.displayObject[varName] = newValue;
            }
        }
        // -- 刷新操作工具对象（延迟到下一帧）
        Callback.CallLaterBeforeRender(previewWin.operateObjectTool.refresh, previewWin.operateObjectTool);
    }
}
