//------------------------------------------------------------------------------------------------------
// 图像层相关 遍历每个已使用的通道，每个已使用的通道再遍历已使用的指令，调用下面的函数
// [everyone passage] x [everyone used command type]
//------------------------------------------------------------------------------------------------------
/**
 * 清理函数：每次刷新前调用此函数进行清理
 * @param passageID 通道
 * @param passageData 通道储存的数据
 */
function imageLayerPassageClear(passageID: number, passageData: UIPassageData) {
    // -- 判断该通道是图片的话
    if (passageData.type == passageType) {
        // 清理注册的移动
        if (passageData.timeFunc) {
            os.remove_ENTERFRAME(passageData.timeFunc, this);
        }
        var a: GUI_BASE = passageData.displayObject;
        // 存在且不是来自显示图片的话
        if (a && !passageData.displayObjectFromShow) {
            a.dispose();
        }
    }
}
/**
 * 开始函数
 * @param passageID 通道
 * @param passageData 通道储存的数据
 */
function imageLayerPassageStart(passageID: number, passageData: UIPassageData) {
    // -- 当前通道数据是图片类别的话
    if (passageData.type == passageType) {
        // -- 不存在图片显示对象的话，还是要显示一下图片移动
        if (!passageData.displayObject) {
            if (passageData.showType == 1) {
                var showUIID = passageData.refObjectEnabled ? passageData.refObject : 0;
            }
            else {
                var needShowRefUI = passageData.showType == 1 || (passageData.showType == 0 && passageData.objectUseVar);
                var showUIID = needShowRefUI && passageData.refObjectEnabled ? passageData.refObject : passageData.uiID;
            }
            var a = GameUI.load(showUIID, true);
            if (!a) {
                return;
            }
            // a.syncLoadWhenAssetExist = true;
            previewWin.operateObjectTool.addExcludeOperactionObject(a);
            a.data = passageData;

            passageData.displayObject = a;
            if (passageData.showType == 1) {
                imageLayer.addChild(a);
                // -- 同Z轴时按该优先级出现，即编号越大yue
                a.dpDisplayPriority = passageID;
                a.width = Config.WINDOW_WIDTH;
                a.height = Config.WINDOW_HEIGHT;
                // -- 深度坐标
                a.useDPCoordScaleMode = true;
                a.useDPCoord = true;
                a.dpX = passageData.dpX;
                a.dpY = passageData.dpY;
                a.dpZ = passageData.dpZ;
                a.dpScaleX = passageData.dpScaleX;
                a.dpScaleY = passageData.dpScaleY;
                a.rotation1 = passageData.rotation;
                a.opacity = passageData.opacity;
            }
            else {
                uiLayer.addChild(a);
                a.dpDisplayPriority = passageData.dpDisplayPriority;
                a.x = passageData.dpX;
                a.y = passageData.dpY;
                a.scaleX = passageData.dpScaleX;
                a.scaleY = passageData.dpScaleY;
                a.width = Config.WINDOW_WIDTH;
                a.height = Config.WINDOW_HEIGHT;
                a.rotation = passageData.rotation;
                a.opacity = passageData.opacity;
            }
            // -- 如果是当前指令的话则选中它
            if (previewWin.currentSelectCmd == passageData.cmd) {
                Callback.CallLaterBeforeRender(() => {
                    previewWin.operateObjectTool.selectObjects([a]);
                }, this)
            }
            else if (!previewWin.allowAllDisplayObject) {
                a.mouseEnabled = false;
                a.setTonal(-200, -200, -200, 0);
            }
            // ---
            a.on(EventObject.RIGHT_MOUSE_UP, this, () => {
                if (previewWin.viewAreaRightMouseMove) return;
                var menuData: any[] = [
                    {
                        label: "定位到对应的事件",
                        splitLine: false,// 是否显示分割线
                        children: [], // 存放和本身这个对象是一样的格式
                        execute: "selectEvent", // 点击后执行的方法名称
                        shortcutKey: "F", //显示的快捷键名称
                        menuHotKey: "F", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                        enabled: true, // 是否可用，不填写表示true
                        visible: true, // 是否显示，不填写表示true
                    },
                    {
                        label: "生成「显示界面事件」",
                        splitLine: false,// 是否显示分割线
                        children: [], // 存放和本身这个对象是一样的格式
                        execute: "createShowUIEvent", // 点击后执行的方法名称
                        shortcutKey: "", //显示的快捷键名称
                        menuHotKey: "", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                        enabled: true, // 是否可用，不填写表示true
                        visible: true, // 是否显示，不填写表示true
                    },
                    {
                        label: "生成「移动界面事件」",
                        splitLine: false,// 是否显示分割线
                        children: [], // 存放和本身这个对象是一样的格式
                        execute: "createMoveUIEvent", // 点击后执行的方法名称
                        shortcutKey: "", //显示的快捷键名称
                        menuHotKey: "", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                        enabled: true, // 是否可用，不填写表示true
                        visible: true, // 是否显示，不填写表示true
                    },
                    {
                        label: "生成「关闭界面事件」",
                        splitLine: false,// 是否显示分割线
                        children: [], // 存放和本身这个对象是一样的格式
                        execute: "earseEvent", // 点击后执行的方法名称
                        shortcutKey: "D", //显示的快捷键名称
                        menuHotKey: "D", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                        enabled: true, // 是否可用，不填写表示true
                        visible: true, // 是否显示，不填写表示true
                    }
                ];
                // 关于生成的指令是插入在上方还是下方
                var isinsertCMDToUp = passageData.cmd == previewWin.currentSelectCmd ? false : true;
                var funcDomain = {
                    selectEvent: () => {
                        previewWin.selectCmdMessageByCmd(passageData.cmd, true);
                    },
                    createShowUIEvent: () => {
                        var d = a.data as UIPassageData;
                        createUIMoveData(a, d);
                        var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_showUI, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                        newCmdData.cmd = newCMD;
                        newCmdParams = null;
                        previewWin.setData();
                    },
                    createMoveUIEvent: () => {
                        var d = a.data as UIPassageData;
                        createUIMoveData(a, d);
                        var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_moveUI, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                        newCmdData.cmd = newCMD;
                        newCmdParams = null;
                        previewWin.setData();
                    },
                    earseEvent: () => {
                        // 如果是使用图像层的话则关闭它，否则关闭界面
                        var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_closeUI, {
                            showType: passageData.showType,
                            passageID: passageData.passageID,
                            uiID: passageData.uiID
                        }, isinsertCMDToUp ? -2 : -3, false);
                        previewWin.setData();
                    }
                };
                previewWin.createMenu(menuData, stage.mouseX, stage.mouseY, funcDomain);
            });
        }
    }
}
//------------------------------------------------------------------------------------------------------
// 自定义
//------------------------------------------------------------------------------------------------------
function createUIMoveData(sp: GameSprite, data: UIPassageData) {
    newCmdParams = ObjectUtils.depthClone(data.lastStandAvatarCmdParams);
    if (data.showType == 1) {
        newCmdParams.dpX = sp.dpX;
        newCmdParams.dpY = sp.dpY;
        newCmdParams.dpScaleX = sp.dpScaleX;
        newCmdParams.dpScaleY = sp.dpScaleY;
    }
    else {
        newCmdParams.dpX = sp.x;
        newCmdParams.dpY = sp.y;
        newCmdParams.dpScaleX = sp.scaleX;
        newCmdParams.dpScaleY = sp.scaleY;
    }
    newCmdParams.dpZ = sp.dpZ;
    newCmdParams.rotation = sp.rotation1;
    newCmdParams.passageID = data.passageID;
    newCmdData = data;
}
/**
 * 当来自操作工具的属性修改时
 * @param type 类别 0-移动 1-缩放 2-旋转  
 * @param realSelObjs 选中的工具
 */
function imageLayerOnToolsChange(type: number, realSelObjs: GameSprite[]) {
    // 根据选中的数据进行获取
    for (var i = 0; i < realSelObjs.length; i++) {
        var sp = realSelObjs[i];
        var data: UIPassageData = sp.data;
        // 如果存在数据且数据类别是当前指令的类别时
        if (data && data.type == passageType && data.cmd.customID == commandID) {
            // 实际坐标转化为深度坐标
            var res = sp.realCoordToDPCoord(type == 0, type == 1);
            // 选中的指令是当前指令时：则直接修改属性
            if (previewWin.currentSelectCmd == data.cmd) {
                var passageID: number = data.passageID as any;
                if (type == 0) {
                    if (data.showType == 0) {
                        previewWin.modifyCommandParam(data.cmd, "dpX", sp.x);
                        previewWin.modifyCommandParam(data.cmd, "dpY", sp.y);
                    }
                    else {
                        previewWin.modifyCommandParam(data.cmd, "dpX", sp.dpX);
                        previewWin.modifyCommandParam(data.cmd, "dpY", sp.dpY);
                    }
                }
                else if (type == 1) {
                    if (data.showType == 0) {
                        previewWin.modifyCommandParam(data.cmd, "dpScaleX", sp.scaleX);
                        previewWin.modifyCommandParam(data.cmd, "dpScaleY", sp.scaleY);
                    }
                    else {
                        previewWin.modifyCommandParam(data.cmd, "dpScaleX", res.dpScaleX);
                        previewWin.modifyCommandParam(data.cmd, "dpScaleY", res.dpScaleY);
                    }
                }
                else {
                    previewWin.modifyCommandParam(data.cmd, "rotation", sp.rotation);
                }
            }
            else {
                // 不存在数据时则创建
                if (newCmdParams == null) {
                    createUIMoveData(sp, data);
                }
                // 否则直接修改
                else {
                    if (type == 0) {
                        if (data.showType == 0) {
                            newCmdParams.dpX = sp.x;
                            newCmdParams.dpY = sp.y;
                        }
                        else {
                            newCmdParams.dpX = sp.dpX;
                            newCmdParams.dpY = sp.dpY;
                        }
                    }
                    else if (type == 1) {
                        if (data.showType == 0) {
                            newCmdParams.dpScaleX = sp.scaleX;
                            newCmdParams.dpScaleY = sp.scaleY;
                        }
                        else {
                            newCmdParams.dpScaleX = res.dpScaleX;
                            newCmdParams.dpScaleY = res.dpScaleY;
                        }
                    }
                    else {
                        newCmdParams.rotation = sp.rotation;
                    }
                }
            }
        }
    }
}

3011.cmdPreviewJS:
var passageType = "gcUI";
var PLUGIN_CUSTOM_COMMAND_showUI = 3010;
var PLUGIN_CUSTOM_COMMAND_moveUI = 3011;
var PLUGIN_CUSTOM_COMMAND_closeUI = 3012;
var newCmdParams;
var newCmdData;
function runCommand(cmdParams, cmd, isPlayMode) {
    var _this = this;
    if (cmdParams.showType == 1) {
        var passageID = cmdParams.passageID;
        if (cmdParams.passageIDUseVar) {
            return;
        }
    }
    else {
        passageID = 1000000 + cmdParams.uiID;
    }
    var passageData = imageLayerPassageData[passageID];
    if (isPlayMode) {
        if (!passageData || passageData.type != passageType || !passageData.displayObject)
            return;
        if (passageData.timeFunc) {
            os.remove_ENTERFRAME(passageData.timeFunc, this);
        }
        var toDpX = cmdParams.dpX;
        var toDpY = cmdParams.dpY;
        var toDpZ = cmdParams.dpZ;
        var toDpScaleX = cmdParams.dpScaleX;
        var toDpScaleY = cmdParams.dpScaleY;
        var toRotation = cmdParams.rotation;
        var toOpacity = cmdParams.opacity;
        if (cmdParams.timeType == 0) {
            if (cmdParams.showType == 1) {
                passageData.displayObject.dpX = toDpX;
                passageData.displayObject.dpY = toDpY;
                passageData.displayObject.dpZ = toDpZ;
                passageData.displayObject.dpScaleX = toDpScaleX;
                passageData.displayObject.dpScaleY = toDpScaleY;
                passageData.displayObject.dpCoordToRealCoord();
            }
            else {
                passageData.displayObject.x = toDpX;
                passageData.displayObject.y = toDpY;
                passageData.displayObject.scaleX = toDpScaleX;
                passageData.displayObject.scaleY = toDpScaleY;
            }
            passageData.displayObject.rotation1 = toRotation;
            passageData.displayObject.opacity = toOpacity;
        }
        else {
            if (cmdParams.showType == 1) {
                var cX = passageData.displayObject.dpX;
                var cY = passageData.displayObject.dpY;
                var cScaleX = passageData.displayObject.dpScaleX;
                var cScaleY = passageData.displayObject.dpScaleY;
            }
            else {
                var cX = passageData.displayObject.x;
                var cY = passageData.displayObject.y;
                var cScaleX = passageData.displayObject.scaleX;
                var cScaleY = passageData.displayObject.scaleY;
            }
            var m = {
                time: cmdParams.time,
                curTime: 1,
                x: cX,
                y: cY,
                z: passageData.displayObject.dpZ,
                scaleX: cScaleX,
                scaleY: cScaleY,
                rotation: passageData.displayObject.rotation1,
                opacity: passageData.displayObject.opacity,
                x2: toDpX - cX,
                y2: toDpY - cY,
                z2: toDpZ - passageData.displayObject.dpZ,
                scaleX2: toDpScaleX - cScaleX,
                scaleY2: toDpScaleY - cScaleY,
                rotation2: toRotation - passageData.displayObject.rotation1,
                opacity2: toOpacity - passageData.displayObject.opacity,
                transData: GameUtils.getTransData(cmdParams.trans)
            };
            var f;
            os.add_ENTERFRAME(f = passageData.timeFunc = function (displayObject, cmdParams) {
                var per = m.curTime / m.time;
                var value = GameUtils.getValueByTransData(m.transData, per);
                if (cmdParams.showType == 1) {
                    displayObject.dpX = m.x2 * value + m.x;
                    displayObject.dpY = m.y2 * value + m.y;
                    displayObject.dpZ = m.z2 * value + m.z;
                    displayObject.dpScaleX = m.scaleX2 * value + m.scaleX;
                    displayObject.dpScaleY = m.scaleY2 * value + m.scaleY;
                    displayObject.dpCoordToRealCoord();
                }
                else {
                    displayObject.x = m.x2 * value + m.x;
                    displayObject.y = m.y2 * value + m.y;
                    displayObject.scaleX = m.scaleX2 * value + m.scaleX;
                    displayObject.scaleY = m.scaleY2 * value + m.scaleY;
                }
                displayObject.rotation1 = m.rotation2 * value + m.rotation;
                displayObject.opacity = m.opacity2 * value + m.opacity;
                m.curTime++;
                if (per == 1) {
                    os.remove_ENTERFRAME(arguments.callee, _this);
                }
            }, this, [passageData.displayObject, cmdParams]);
            f.apply(this, [passageData.displayObject, cmdParams]);
            var lastOnCoverPassage = passageData.onCoverPassage;
            passageData.onCoverPassage = function () {
                os.remove_ENTERFRAME(f, this);
                lastOnCoverPassage.apply(this, arguments);
            };
        }
    }
    else {
        if (passageData && passageData.type != passageType)
            return;
        if (!passageData) {
            passageData = imageLayerPassageData[passageID] = {};
            if (cmdParams.showType == 0) {
                passageData.dpDisplayPriority = previewWin.uiLayerDisplayPriority++;
            }
        }
        passageData.passageID = passageID;
        passageData.type = passageType;
        passageData.cmd = cmd;
        if (!passageData.lastStandAvatarCmdParams) {
            passageData.lastStandAvatarCmdParams = cmdParams;
        }
        else {
            ObjectUtils.clone(cmdParams, passageData.lastStandAvatarCmdParams);
        }
        passageData.dpX = cmdParams.dpX;
        passageData.dpY = cmdParams.dpY;
        passageData.dpZ = cmdParams.dpZ;
        passageData.dpScaleX = cmdParams.dpScaleX;
        passageData.dpScaleY = cmdParams.dpScaleY;
        passageData.rotation = cmdParams.rotation;
        passageData.opacity = cmdParams.opacity;
        passageData.showType = cmdParams.showType;
        passageData.objectUseVar = cmdParams.objectUseVar;
        passageData.setAttr = true;
        if (!passageData.displayObjectFromShow) {
            if (!passageData.uiID && !cmdParams.objectUseVar)
                passageData.uiID = cmdParams.uiID;
            if (cmdParams.refObjectEnabled) {
                passageData.refObjectEnabled = cmdParams.refObjectEnabled;
                passageData.refObject = cmdParams.refObject;
            }
        }
    }
}
function onCommandAttributeBeforeChange(cmd, cmdParams, varName) {
    if (varName == "passageID" || varName == "passageIDUseVar" || varName == "uiID" || varName == "refObject" || varName == "refObjectEnabled" || varName == "objectUseVar" || varName == "showType") {
        previewWin.clearData();
    }
}
function onCommandAttributeChange(cmd, cmdParams, varName, newValue) {
    if (varName == "passageID" || varName == "passageIDUseVar" || varName == "uiID" || varName == "refObject" || varName == "refObjectEnabled" || varName == "objectUseVar" || varName == "showType") {
        previewWin.setData(false);
        return;
    }
    if (cmdParams.showType == 1) {
        var passageID = cmdParams.passageID;
        if (cmdParams.passageIDUseVar) {
            return;
        }
    }
    else {
        passageID = 1000000 + cmdParams.uiID;
    }
    var passageData = imageLayerPassageData[passageID];
    if (!passageData)
        return;
    if (passageData.type == passageType && passageData.displayObject) {
        if (varName == "rotation") {
            passageData.displayObject.rotation1 = newValue;
        }
        else {
            if (cmdParams.showType == 0) {
                var attrNameMapping = { dpX: "x", dpY: "y", dpScaleX: "scaleX", dpScaleY: "scaleY" };
                if (attrNameMapping[varName])
                    passageData.displayObject[attrNameMapping[varName]] = newValue;
                else
                    passageData.displayObject[varName] = newValue;
            }
            else {
                passageData.displayObject[varName] = newValue;
            }
        }
        Callback.CallLaterBeforeRender(previewWin.operateObjectTool.refresh, previewWin.operateObjectTool);
    }
}
function imageLayerOnToolsChangePostion(realSelObjs) {
    imageLayerOnToolsChange(0, realSelObjs);
}
function imageLayerOnToolsChangeSize(realSelObjs) {
    imageLayerOnToolsChange(1, realSelObjs);
}
function imageLayerOnToolsChangeRotation(realSelObjs) {
    imageLayerOnToolsChange(2, realSelObjs);
}
function imageLayerOnToolsChangeStart(realSelObjs) {
}
function imageLayerOnToolsChangeEnd(realSelObjs) {
    if (newCmdParams == null)
        return;
    if (realSelObjs.length == 0)
        return;
    var data = realSelObjs[0].data;
    if (!data || data.type != passageType)
        return;
    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_moveUI, newCmdParams, -2, false);
    newCmdData.cmd = newCMD;
    newCmdParams = null;
    previewWin.setData();
}
function imageLayerOnToolsSelectObject(realSelObjs) {
}
function imageLayerPassageClear(passageID, passageData) {
    if (passageData.type == passageType) {
        if (passageData.timeFunc) {
            os.remove_ENTERFRAME(passageData.timeFunc, this);
        }
        var a = passageData.displayObject;
        if (a && !passageData.displayObjectFromShow) {
            a.dispose();
        }
    }
}
function imageLayerPassageStart(passageID, passageData) {
    if (passageData.type == passageType) {
        if (!passageData.displayObject) {
            if (passageData.showType == 1) {
                var showUIID = passageData.refObjectEnabled ? passageData.refObject : 0;
            }
            else {
                var needShowRefUI = passageData.showType == 1 || (passageData.showType == 0 && passageData.objectUseVar);
                var showUIID = needShowRefUI && passageData.refObjectEnabled ? passageData.refObject : passageData.uiID;
            }
            var a = GameUI.load(showUIID, true);
            if (!a) {
                return;
            }
            previewWin.operateObjectTool.addExcludeOperactionObject(a);
            a.data = passageData;
            passageData.displayObject = a;
            if (passageData.showType == 1) {
                imageLayer.addChild(a);
                a.dpDisplayPriority = passageID;
                a.width = Config.WINDOW_WIDTH;
                a.height = Config.WINDOW_HEIGHT;
                a.useDPCoordScaleMode = true;
                a.useDPCoord = true;
                a.dpX = passageData.dpX;
                a.dpY = passageData.dpY;
                a.dpZ = passageData.dpZ;
                a.dpScaleX = passageData.dpScaleX;
                a.dpScaleY = passageData.dpScaleY;
                a.rotation1 = passageData.rotation;
                a.opacity = passageData.opacity;
            }
            else {
                uiLayer.addChild(a);
                a.dpDisplayPriority = passageData.dpDisplayPriority;
                a.x = passageData.dpX;
                a.y = passageData.dpY;
                a.scaleX = passageData.dpScaleX;
                a.scaleY = passageData.dpScaleY;
                a.width = Config.WINDOW_WIDTH;
                a.height = Config.WINDOW_HEIGHT;
                a.rotation = passageData.rotation;
                a.opacity = passageData.opacity;
            }
            if (previewWin.currentSelectCmd == passageData.cmd) {
                Callback.CallLaterBeforeRender(function () {
                    previewWin.operateObjectTool.selectObjects([a]);
                }, this);
            }
            else if (!previewWin.allowAllDisplayObject) {
                a.mouseEnabled = false;
                a.setTonal(-200, -200, -200, 0);
            }
            a.on(EventObject.RIGHT_MOUSE_UP, this, function () {
                if (previewWin.viewAreaRightMouseMove)
                    return;
                var menuData = [
                    {
                        label: "定位到对应的事件",
                        splitLine: false,
                        children: [],
                        execute: "selectEvent",
                        shortcutKey: "F",
                        menuHotKey: "F",
                        enabled: true,
                        visible: true,
                    },
                    {
                        label: "生成「显示界面事件」",
                        splitLine: false,
                        children: [],
                        execute: "createShowUIEvent",
                        shortcutKey: "",
                        menuHotKey: "",
                        enabled: true,
                        visible: true,
                    },
                    {
                        label: "生成「移动界面事件」",
                        splitLine: false,
                        children: [],
                        execute: "createMoveUIEvent",
                        shortcutKey: "",
                        menuHotKey: "",
                        enabled: true,
                        visible: true,
                    },
                    {
                        label: "生成「关闭界面事件」",
                        splitLine: false,
                        children: [],
                        execute: "earseEvent",
                        shortcutKey: "D",
                        menuHotKey: "D",
                        enabled: true,
                        visible: true,
                    }
                ];
                var isinsertCMDToUp = passageData.cmd == previewWin.currentSelectCmd ? false : true;
                var funcDomain = {
                    selectEvent: function () {
                        previewWin.selectCmdMessageByCmd(passageData.cmd, true);
                    },
                    createShowUIEvent: function () {
                        var d = a.data;
                        createUIMoveData(a, d);
                        var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_showUI, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                        newCmdData.cmd = newCMD;
                        newCmdParams = null;
                        previewWin.setData();
                    },
                    createMoveUIEvent: function () {
                        var d = a.data;
                        createUIMoveData(a, d);
                        var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_moveUI, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                        newCmdData.cmd = newCMD;
                        newCmdParams = null;
                        previewWin.setData();
                    },
                    earseEvent: function () {
                        var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_closeUI, {
                            showType: passageData.showType,
                            passageID: passageData.passageID,
                            uiID: passageData.uiID
                        }, isinsertCMDToUp ? -2 : -3, false);
                        previewWin.setData();
                    }
                };
                previewWin.createMenu(menuData, stage.mouseX, stage.mouseY, funcDomain);
            });
        }
    }
}
function createUIMoveData(sp, data) {
    newCmdParams = ObjectUtils.depthClone(data.lastStandAvatarCmdParams);
    if (data.showType == 1) {
        newCmdParams.dpX = sp.dpX;
        newCmdParams.dpY = sp.dpY;
        newCmdParams.dpScaleX = sp.dpScaleX;
        newCmdParams.dpScaleY = sp.dpScaleY;
    }
    else {
        newCmdParams.dpX = sp.x;
        newCmdParams.dpY = sp.y;
        newCmdParams.dpScaleX = sp.scaleX;
        newCmdParams.dpScaleY = sp.scaleY;
    }
    newCmdParams.dpZ = sp.dpZ;
    newCmdParams.rotation = sp.rotation1;
    newCmdParams.passageID = data.passageID;
    newCmdData = data;
}
function imageLayerOnToolsChange(type, realSelObjs) {
    for (var i = 0; i < realSelObjs.length; i++) {
        var sp = realSelObjs[i];
        var data = sp.data;
        if (data && data.type == passageType && data.cmd.customID == commandID) {
            var res = sp.realCoordToDPCoord(type == 0, type == 1);
            if (previewWin.currentSelectCmd == data.cmd) {
                var passageID = data.passageID;
                if (type == 0) {
                    if (data.showType == 0) {
                        previewWin.modifyCommandParam(data.cmd, "dpX", sp.x);
                        previewWin.modifyCommandParam(data.cmd, "dpY", sp.y);
                    }
                    else {
                        previewWin.modifyCommandParam(data.cmd, "dpX", sp.dpX);
                        previewWin.modifyCommandParam(data.cmd, "dpY", sp.dpY);
                    }
                }
                else if (type == 1) {
                    if (data.showType == 0) {
                        previewWin.modifyCommandParam(data.cmd, "dpScaleX", sp.scaleX);
                        previewWin.modifyCommandParam(data.cmd, "dpScaleY", sp.scaleY);
                    }
                    else {
                        previewWin.modifyCommandParam(data.cmd, "dpScaleX", res.dpScaleX);
                        previewWin.modifyCommandParam(data.cmd, "dpScaleY", res.dpScaleY);
                    }
                }
                else {
                    previewWin.modifyCommandParam(data.cmd, "rotation", sp.rotation);
                }
            }
            else {
                if (newCmdParams == null) {
                    createUIMoveData(sp, data);
                }
                else {
                    if (type == 0) {
                        if (data.showType == 0) {
                            newCmdParams.dpX = sp.x;
                            newCmdParams.dpY = sp.y;
                        }
                        else {
                            newCmdParams.dpX = sp.dpX;
                            newCmdParams.dpY = sp.dpY;
                        }
                    }
                    else if (type == 1) {
                        if (data.showType == 0) {
                            newCmdParams.dpScaleX = sp.scaleX;
                            newCmdParams.dpScaleY = sp.scaleY;
                        }
                        else {
                            newCmdParams.dpScaleX = res.dpScaleX;
                            newCmdParams.dpScaleY = res.dpScaleY;
                        }
                    }
                    else {
                        newCmdParams.rotation = sp.rotation;
                    }
                }
            }
        }
    }
}

3011.customPhraseDescTS:
// 根据属性名称值编写自定义短语描述，如："我的短语是"+attrs.xxx
// 使用下拉框组件的变量attrs.xxx有对应的attrs.xxxItems的属性以便获得标签名集合
// 获取自定义模块的数据名称的函数：getModuleDataName(moduleID:number,dataID:number):string;
// 获取各种列表内数据名称的函数：getTypeDataListName(typeID: number, dataID: number):string;
// -- typeID ：0-全局变量 1-全局开关 2-全局字符串 3-玩家变量 4-玩家开关 5-玩家字符串
//           ：6-场景列表 7-场景对象模型列表 8-图块列表 9-行走图列表 10-行走图动作列表 11-立绘列表 12-立绘表情列表
//           ：13-公共事件 14-对话框列表 15-动画列表 16-动画信号列表 17-界面列表 18-数据结构列表 19-自定义模块列表
//           ：20-自定义命令列表 21-自定义行为列表 22-自动元件列表 23-行走图部件列表 24-立绘部件列表
var str = ``;
if (attrs.showType == 1) {
    var passageIDStr = attrs.passageIDUseVar ? `@${attrs.passageIDVar}-${getTypeDataListName(3, attrs.passageIDVar)}` : attrs.passageID;
    str += `编号【${passageIDStr}】`;
}

var objStr = attrs.objectUseVar ? `@${attrs.uiVar}-${getTypeDataListName(3, attrs.uiVar)}` : `${attrs.uiID}-${getTypeDataListName(17, attrs.uiID)}`;
str += `「${objStr}」`;

str += `${attrs.timeTypeItems[attrs.timeType]} `;
if (attrs.timeType == 1) {
    str += attrs.time + "帧 "
}

if (attrs.posUseVar) {
    var xStr = `@${attrs.dpXVar}`;
    var yStr = `@${attrs.dpYVar}`;
}
else {
    xStr = attrs.dpX as any;
    yStr = attrs.dpY as any;
}
var zStr = attrs.zUseVar ? `@${attrs.dpZVar}` : attrs.dpZ;
if (attrs.sizeUseVar) {
    var wStr = `@${attrs.dpScaleX}`;
    var hStr = `@${attrs.dpScaleY}`;
}
else {
    wStr = Math.floor(attrs.dpScaleX * 100) + "%";
    hStr = Math.floor(attrs.dpScaleY * 100) + "%";
}
var roStr = attrs.rotationUseVar ? `@${attrs.rotationVar}` : attrs.rotation;
var alphaStr = attrs.opacityUseVar ? `@${attrs.opacityVar}` : attrs.opacity;
str += ` 坐标(${xStr},${yStr},${zStr}) 尺寸(${wStr},${hStr}) 旋转度=${roStr} 透明度=${alphaStr}`;

str;

