function imageLayerPassageClear(passageID, passageData) {
    if (passageData.type == passageType) {
        imageLayer.clearMaterials();
        for (var i = 0; i < passageData.imagePassages.length; i++) {
            var imagePassageInfo = passageData.imagePassages[i];
            var targetPassageID = imagePassageInfo.passageID;
            var targetPassageData = imageLayerPassageData[targetPassageID];
            if (targetPassageData && targetPassageData.displayObject) {
                targetPassageData.displayObject.clearMaterials();
            }
        }
        for (var i = 0; i < passageData.uis.length; i++) {
            var uiPassageInfo = passageData.uis[i];
            var targetUIID = uiPassageInfo.uiID;
            var targetPassageID = 1000000 + targetUIID;
            var targetPassageData = imageLayerPassageData[targetPassageID];
            if (targetPassageData && targetPassageData.displayObject) {
                targetPassageData.displayObject.clearMaterials();
            }
        }
        passageData.stages = [];
        passageData.imagePassages = [];
        passageData.uis = [];
    }
}
function imageLayerPassageStart(passageID, passageData) {
    if (passageData.type == passageType) {
        for (var i = 0; i < passageData.stages.length; i++) {
            var stageInfos = passageData.stages[i];
            for (var p = 0; p < stageInfos.length; p++) {
                var stageInfo = stageInfos[p];
                for (var s = 0; s < stageInfo.materials.length; s++) {
                    var mData = stageInfo.materials[s];
                    var m = imageLayer.getMaterialByID(mData.id, p);
                    if (m)
                        imageLayer.removeMaterial(mData, p);
                    imageLayer.addMaterial(mData, p);
                }
            }
        }
        imageLayer.setMaterialDirty();
        for (var i = 0; i < passageData.imagePassages.length; i++) {
            var imagePassageInfos = passageData.imagePassages[i];
            var targetDisplayObjectPassage = imageLayerPassageData[imagePassageInfos.passageID];
            if (!targetDisplayObjectPassage || !targetDisplayObjectPassage.displayObject)
                continue;
            for (var p = 0; p < imagePassageInfos.materialSetting.length; p++) {
                var materialSetting = imagePassageInfos.materialSetting[p];
                for (var s = 0; s < materialSetting.materials.length; s++) {
                    var mData = materialSetting.materials[s];
                    var m = targetDisplayObjectPassage.displayObject.getMaterialByID(mData.id, p);
                    if (m)
                        targetDisplayObjectPassage.displayObject.removeMaterial(mData, p);
                    targetDisplayObjectPassage.displayObject.addMaterial(mData, p);
                    targetDisplayObjectPassage.displayObject.setMaterialDirty();
                }
            }
        }
        for (var i = 0; i < passageData.uis.length; i++) {
            var uiPassageInfos = passageData.uis[i];
            var targetPassageID = 1000000 + uiPassageInfos.uiID;
            var targetDisplayObjectPassage = imageLayerPassageData[targetPassageID];
            if (!targetDisplayObjectPassage || !targetDisplayObjectPassage.displayObject)
                continue;
            for (var p = 0; p < uiPassageInfos.materialSetting.length; p++) {
                var materialSetting = uiPassageInfos.materialSetting[p];
                for (var s = 0; s < materialSetting.materials.length; s++) {
                    var mData = materialSetting.materials[s];
                    var m = targetDisplayObjectPassage.displayObject.getMaterialByID(mData.id, p);
                    if (m)
                        targetDisplayObjectPassage.displayObject.removeMaterial(mData, p);
                    targetDisplayObjectPassage.displayObject.addMaterial(mData, p);
                    targetDisplayObjectPassage.displayObject.setMaterialDirty();
                }
            }
        }
    }
}

3015.customPhraseDescTS:
// 根据属性名称值编写自定义短语描述，如："我的短语是"+attrs.xxx
// 使用下拉框组件的变量attrs.xxx有对应的attrs.xxxItems的属性以便获得标签名集合
// 获取自定义模块的数据名称的函数：getModuleDataName(moduleID:number,dataID:number):string;
// 获取各种列表内数据名称的函数：getTypeDataListName(typeID: number, dataID: number):string;
// -- typeID ：0-全局变量 1-全局开关 2-全局字符串 3-玩家变量 4-玩家开关 5-玩家字符串
//           ：6-场景列表 7-场景对象模型列表 8-图块列表 9-行走图列表 10-行走图动作列表 11-立绘列表 12-立绘表情列表
//           ：13-公共事件 14-对话框列表 15-动画列表 16-动画信号列表 17-界面列表 18-数据结构列表 19-自定义模块列表
//           ：20-自定义命令列表 21-自定义行为列表 22-自动元件列表 23-行走图部件列表 24-立绘部件列表
var str = ``;
if (attrs.targetType == 0) {
    str += `图像层`;
}
else if (attrs.targetType == 1) {
    var passageIDStr = attrs.passageIDUseVar ? `@${attrs.passageIDVar}-${getTypeDataListName(3, attrs.passageIDVar)}` : attrs.passageID;
    str += `图像编号【${passageIDStr}】`;
}
else if (attrs.targetType == 2) {
    var objStr = attrs.objectUseVar ? `@${attrs.uiVar}-${getTypeDataListName(3, attrs.uiVar)}` : `${attrs.uiID}-${getTypeDataListName(17, attrs.uiID)}`;
    str += `界面「${objStr} 」`;
}
var mNameArr = [];
if (attrs.materialData) {
    for (var i in attrs.materialData) {
        var m = attrs.materialData[i]
        if (m && m.materials) {
            for (var s = 0; s < m.materials.length; s++) {
                var mData = m.materials[s];
                var name = getTypeDataListName(25, mData.id);
                mNameArr.push(`${mData.id}-${name}`);
            }
        }
    }
}
if (mNameArr.length > 0) {
    str += `：\n -- ` + mNameArr.join("\n -- ");
}
str;

