function runCommand(cmdParams, cmd, isPlayMode) {
    var _this = this;
    if (cmdParams.passageIDUseVar) {
        return;
    }
    var passageData = imageLayerPassageData[cmdParams.passageID];
    if (isPlayMode) {
        if (!passageData || passageData.type != passageType || !passageData.displayObject)
            return;
        if (passageData.timeFunc) {
            os.remove_ENTERFRAME(passageData.timeFunc, this);
        }
        var toDpX = cmdParams.dpX;
        var toDpY = cmdParams.dpY;
        var toDpZ = cmdParams.dpZ;
        var toDpWidth = cmdParams.dpWidth;
        var toDpHeight = cmdParams.dpHeight;
        var toRotation = cmdParams.rotation;
        var toOpacity = cmdParams.opacity;
        if (cmdParams.timeType == 0) {
            passageData.displayObject.dpX = toDpX;
            passageData.displayObject.dpY = toDpY;
            passageData.displayObject.dpZ = toDpZ;
            passageData.displayObject.dpWidth = toDpWidth;
            passageData.displayObject.dpHeight = toDpHeight;
            passageData.displayObject.rotation1 = toRotation;
            passageData.displayObject.opacity = toOpacity;
            passageData.displayObject.flip = cmdParams.flip;
        }
        else {
            var m = {
                time: cmdParams.time,
                curTime: 1,
                x: passageData.displayObject.dpX,
                y: passageData.displayObject.dpY,
                z: passageData.displayObject.dpZ,
                width: passageData.displayObject.dpWidth,
                height: passageData.displayObject.dpHeight,
                rotation: passageData.displayObject.rotation1,
                opacity: passageData.displayObject.opacity,
                x2: toDpX - passageData.displayObject.dpX,
                y2: toDpY - passageData.displayObject.dpY,
                z2: toDpZ - passageData.displayObject.dpZ,
                width2: toDpWidth - passageData.displayObject.dpWidth,
                height2: toDpHeight - passageData.displayObject.dpHeight,
                rotation2: toRotation - passageData.displayObject.rotation1,
                opacity2: toOpacity - passageData.displayObject.opacity,
                transData: GameUtils.getTransData(cmdParams.trans)
            };
            var f;
            os.add_ENTERFRAME(f = passageData.timeFunc = function (uiBitmap, cmdParams) {
                if (m.curTime == 1) {
                    uiBitmap.flip = cmdParams.flip;
                }
                var per = m.curTime / m.time;
                var value = GameUtils.getValueByTransData(m.transData, per);
                uiBitmap.dpX = m.x2 * value + m.x;
                uiBitmap.dpY = m.y2 * value + m.y;
                uiBitmap.dpZ = m.z2 * value + m.z;
                uiBitmap.dpWidth = m.width2 * value + m.width;
                uiBitmap.dpHeight = m.height2 * value + m.height;
                uiBitmap.rotation1 = m.rotation2 * value + m.rotation;
                uiBitmap.opacity = m.opacity2 * value + m.opacity;
                m.curTime++;
                if (per == 1) {
                    os.remove_ENTERFRAME(arguments.callee, _this);
                }
                uiBitmap.dpCoordToRealCoord();
            }, this, [passageData.displayObject, cmdParams]);
            f.apply(this, [passageData.displayObject, cmdParams]);
            var lastOnCoverPassage = passageData.onCoverPassage;
            passageData.onCoverPassage = function () {
                os.remove_ENTERFRAME(f, this);
                if (lastOnCoverPassage)
                    lastOnCoverPassage.apply(this, arguments);
            };
        }
    }
    else {
        if (passageData && passageData.type != passageType)
            return;
        if (!passageData)
            passageData = imageLayerPassageData[cmdParams.passageID] = {};
        passageData.passageID = cmdParams.passageID;
        passageData.type = passageType;
        passageData.cmd = cmd;
        if (!passageData.lastImageCmdParams) {
            passageData.lastImageCmdParams = cmdParams;
        }
        else {
            ObjectUtils.clone(cmdParams, passageData.lastImageCmdParams);
        }
        passageData.dpX = cmdParams.dpX;
        passageData.dpY = cmdParams.dpY;
        passageData.dpZ = cmdParams.dpZ;
        passageData.dpWidth = cmdParams.dpWidth;
        passageData.dpHeight = cmdParams.dpHeight;
        passageData.rotation = cmdParams.rotation;
        passageData.opacity = cmdParams.opacity;
        passageData.flip = cmdParams.flip;
    }
}
