    vec2 uv = mf5_convertUV(v_texcoord,mu5_trans,mu5_aspect,1.0);
    #else
    vec2 uv = mf5_convertUV(v_texcoord,mu5_sigma,mu5_aspect,1.0);
    #endif

    if(uv.x >=0.0 && uv.y >= 0.0 && uv.x <= 1.0 && uv.y <= 1.0){
        return texture2D(texture,uv);
    }else{
       return vec4(0.0,0.0,0.0,0.0);
    }
